node.doctext:
[%
#generates messages for a given user
#optional query parameters:
#	fromuser = username of person to filter msgs from
#	messageidstart = digits

return '' if $$USER{user_id} == $HTMLVARS{'guest_user'};
my $str;
use XML::Generator;
use Everything::XML;
my $nl = "\n";
my $UID = getId($USER) || 0;
my $XG = new XML::Generator;

#get messages from a certain user
my $limits = 'for_user='.$UID;
my $filterUser = $query->param('fromuser');
if($filterUser) {
	$filterUser = getNode($filterUser, 'user');
	$filterUser = $filterUser ? $$filterUser{node_id} : 0;
}
$limits .= ' AND author_user='.$filterUser if $filterUser;

#get messages above a certain ID
if( (defined $query->param('messageidstart')) && length($query->param('messageidstart')) ) {
	my $idMin = $query->param('messageidstart');
	if($idMin =~ /^(\d+)$/) {
		$idMin=$1;
		$limits .= ' AND message_id > '.$idMin;
	}
}

my $csr = $DB->sqlSelectMany('*', 'message', $limits, 'order by tstamp, message_id');

my $lines;
my @msgs;
while(my $MSG = $csr->fetchrow_hashref) {
	$lines++;
	push @msgs, $MSG;
}

$str.=$XG->INFO({site => $HTMLVARS{site_url}, sitename => $HTMLVARS{site_name},  servertime => scalar(localtime(time))}, 'Rendered by the Private Message XML Ticker').$nl;
$str .= $XG->info({
	'for_user'=>$UID,
	'for_username'=>Everything::XML::makeXmlSafe($$USER{title}),
	'messagecount'=>scalar(@msgs),
}).$nl;
my $UG;	#current usergroup message is from

foreach my $MSG (@msgs) {
	my $FUSER = getNodeById($$MSG{author_user});
	my $forGroupID = $$MSG{for_usergroup} || 0;
	my $msgInfo = {
#		author => Everything::XML::makeXmlSafe($$FUSER{title}),
		time => $$MSG{tstamp},
		message_id => $$MSG{message_id}
	};

	$$msgInfo{'author'} = (defined $FUSER) ? Everything::XML::makeXmlSafe($$FUSER{title}) : '!!! user with node_id of '.$$MSG{author_user}.' was deleted !!!';	#SourceForge 574781 Removal from usergroups SE!s old ticker

	if($forGroupID) {
		$$msgInfo{for_usergroup_id} = $forGroupID;
		$UG = getNodeById($forGroupID) || undef;
		$$msgInfo{for_usergroup} = (defined $UG) ? $UG->{title} : '!!! usergroup with node_id of '.$forGroupID.' was deleted !!!';
	}

	$str.=$nl."\t".$XG->message($msgInfo, $nl.Everything::XML::makeXmlSafe($$MSG{msgtext}));
}


$nl.$XG->PRIVATE_MESSAGES($str.$nl);

%]
