my @displays;
push @displays, 'display';
push @displays, 'edit';



foreach my $display (@displays) {
	if ($disp eq $display){
		$str .= $display . ' ';
	} else {
		next if ($display eq 'edit' and not canUpdateNode($USER, $NODE));
		$str .= linkNode($NODE, $display, {displaytype => $display}) . ' ';
	}
}



