node.code:
my ($text) = @_;

my ($i,  $j,  $s);
my @vowels = ('acute','grave','uml','circ');

my @parts = split(/(<a\s+(?:title="[^"]*")?\s+href="[^"]*">[^<]*<\/a>)/,$text);
$text = '';
for($i=0;$i<@parts;++$i) {
	if($i==0 && ($parts[$i] =~ /^(\/me\s+)(.*)$/)) {
		$text .= $1;
		$parts[$i] = $2;
	}

	#plain text part
	my @tinyparts = split(/(&[^; ]*[; ])/,$parts[$i]);

	for($j=0;$j<@tinyparts;++$j) {
		#plain text, non-entity
		foreach(split(/(.)/,$tinyparts[$j])) {
			$s = $_;
			if($s=~/[AEIOUaeiou]/) {
				$s = '&'.$s.$vowels[int(rand(4))].';';
			} elsif($s=~/[Nn]/) {
				$s = '&'.$s.'tilde;';
			} elsif($s=~/[Bb]/) {
				$s = '&szlig;';
			}

			$text .= $s;
		}

		#plain text, entity - don't touch
		++$j;
		last if $j>=@tinyparts;
		$text .= $tinyparts[$j];

	}

	#link part - don't do anything with
	++$i;
	last if $i>=@parts;
	$text .= $parts[$i];
}

return $text;
