node.code:
# if an argument: return 1 if current user can publish under this name
# if no argument: return a form control with names of accounts from which user can choose

my $anonymousPublishLevel = 1; # users at or above this level can publish as 'everyone'
my $target = shift;

use Everything::Experience;

return '' unless $USER && $$USER{title} ne 'Guest User'
	and getLevel($USER) >= $anonymousPublishLevel;

my %accounts = (
	everyone => 1
	, Virgil => 'e2docs'
);

@accounts{('Webster 1913', 'EDB', 'Klaproth', 'Cool Man Eddie')} = (1,1,1,1) if htmlcode('isEditor');

if ($target){
	return '' unless $target;

	return 1 if $accounts{$target} == 1
		or $DB -> isApproved($USER, getNode($accounts{$target}, 'usergroup'));

	return '';
}

my @names;
foreach (keys %accounts){
	push @names, $_ if $accounts{$_} == 1
		or $DB -> isApproved($USER, getNode($accounts{$_}, 'usergroup'));
}

my $blah = '<br><small>N.B. By publishing to a different account you cede your
	copyright and lose all control over your writeup</small>';

if (scalar @names == 1){
	return $query -> checkbox(
		-name => 'publishas'
		, value => 'everyone'
		, label => "publish anonymously (as 'everyone')"
	)
	.$blah;

}elsif(@names){
	@names = sort {$a eq 'everyone' ? -1 : $b eq 'everyone' ? 1 : lc($a) cmp lc($b)} @names;
	return $query -> label(
		'publish as:'
		.$query -> popup_menu(
			-name => 'publishas'
			, -values => ['', @names]
			, default => ''
		)
	)
	.$blah;
}

return '';
