node.code:
return if getId($USER) == $HTMLVARS{guest_user};
return if $$USER{title} eq 'everyone';
return ("You are locked in your current room for " . ceil(($$VARS{lockedin} - time)/ 60) . " minutes.<br><br>") if ($$VARS{lockedin} > time);

use Everything::Room;

my $str = ' instant ajax chatterbox_chatter:#' if $query and $query -> param('ajaxTrigger') and
	defined $query->param('changeroom')	and $query->param('changeroom') != $$USER{in_room};
my $RM = getNode('e2 rooms', 'nodegroup');
my @rooms = @{ $$RM{group}  };
my @aprrooms;
my %aprlabel;
my ($nodelet) = @_ ;
$nodelet =~ s/ /+/g;

foreach(@rooms) {
	my $R = getNodeById($_);

	next unless eval($$R{criteria});
	if(defined $query->param('changeroom') and 
    $query->param('changeroom') == $_ and
    $$USER{in_room} != $_)
	{
		changeRoom($USER, $R);
	}

	push @aprrooms, $_;
	$aprlabel{$_} = $$R{title};
}

return unless @aprrooms;

push @aprrooms, '0';
$aprlabel{0}='outside';

if(defined $query->param('changeroom') and $query->param('changeroom') == 0) {
	#steppin' outside
	#$$USER{in_room} = 0;
	#updateNode($USER, -1);
	changeRoom($USER, 0);
	#we should also edit the rooms table
}


#old way
#FIXME: cloaked users should be able to uncloak, even if they can't cloak again
#my $isCloaker = canCloak($USER);

# This replaces Everything::room::canCloak so that c_es can cloak. This is obviously a kludge - asc

my $cloakers = getVars(getNode('cloakers','setting'));
my $isCloaker = (getLevel($USER) >= 10 or exists( $HTMLVARS{'group_content editors'}{$$USER{node_id}}) or isGod($USER) or exists($$cloakers{lc($$USER{title})}));


if($query->param('sexiscool') and $isCloaker) {
	if($query->param('cloaked')) {
		cloak($USER, $VARS);
	} else {
		uncloak($USER, $VARS);
	}
}

my $id = $nodelet ;
$id =~ s/\W//g ;
$nodelet = ":$nodelet" if $nodelet ;
my $ajax = 'ajax '.( $nodelet ? lc($id).':updateNodelet' : 'room_options:changeroom' )
	.'?ajaxTrigger=1&' ;
$str ="<div class='nodelet_section$str' id='room_options'>";
$str.="<h4 class='ns_title'>Room Options</h4>";
$str.=htmlcode('openform');
$str.=$query->checkbox(-name=>'cloaked', checked=>$$VARS{visible}, value=>1, label=>'cloaked',
	class=>$ajax."sexiscool=1&cloaked=/$nodelet") if $isCloaker;

#$str.=htmlcode('lockroom').' '.htmlcode('createroom');
$str.=' '.htmlcode('createroom');

$str.='<br>';
$str.=$query->popup_menu(-name=>'changeroom', Values=>\@aprrooms, default=>$$USER{in_room}, labels=>\%aprlabel,
	class=>$ajax."changeroom=/$nodelet");
$str.=$query->submit('sexiscool','go');
$str.='</form></div>';

$str;
