node.code:
my $displaytype = shift;
$displaytype ||= $query -> param('displaytype') || 'display';

my $displaylike;

my $tinopener = (isGod($USER) and $query -> param('tinopener') and !htmlcode('canseedraft', $NODE));
local ($$NODE{doctext}, $$NODE{collaborators})
	= ("<p><b>&#91;DOCTEXT REDACTED&#93;</b></p>
		<p>You do not have permission to see this draft.</p>" , $$USER{title}) if $tinopener;

if ($$NODE{author_user} == $$USER{node_id}
		|| htmlcode('canseedraft', $NODE)
		|| $$NODE{type}{title} eq 'writeup'){
	$displaylike = getType(
		getNodeWhere({pagetype_nodetype => 117, displaytype => $displaytype}, 'htmlpage') ?
			'writeup' : 'document');
}else{
	$NODE = $GNODE = getNodeById($HTMLVARS{not_found});
	$displaylike = getType($$NODE{type_nodetype});
}

my $PAGE = getPageForType($displaylike, $displaytype);
return parseCode($PAGE -> {page}, $NODE);
