node.code:
#customized display of writeup information
#pass (optional) writeup ID, (optional) things to show
#updated: Wednesday, February 26, 2003 to prepare for making this work for header and/or footer (right now, this seems to do much unneeded caching, but it will serve a useful purpose later)

#
# setup
#

#parameters
my ($wuID, @thisIsNotUsedAnymore) = @_;
my $nID = getId($NODE);
$wuID ||= $nID || 0;
return 'Ack! displayWriteupInfo: Unable to get node_id' unless $wuID;
my $WRITEUP = getNodeById($wuID) || 0;
return 'Ack! displayWriteupInfo: No node with node_id '.$wuID unless $WRITEUP;


#constants
my $UID = getId($USER)||$HTMLVARS{'guest_user'};
my $isGuest = ($UID==$HTMLVARS{'guest_user'});
my $isRoot = !$isGuest && exists $HTMLVARS{group_gods}{$UID};
my $isCE = !$isGuest && ($isRoot || exists $HTMLVARS{'group_content editors'}{$UID});
my $aid = $$WRITEUP{author_user} || 0;
my $wuAuthor = getNodeById($aid) || undef;
my $isMine = (!$isGuest) && ($aid==$UID);
my $authorIsBot = (defined $wuAuthor) && (($wuAuthor->{title} eq 'Webster 1913'));	#FIXME: get (cached) bot setting
my $inPrintMode = $query->param('displaytype') eq 'printable';	#FIXME - is that the best way to see if printable?	#if displaytype=printable - when printing, we don't want things like, for example, voting buttons

#client-side error
local *userWarn = sub {
	return '<small title="displayWriteupInfo (htmlcode)">!!! ' . $_[0] . ' !!!</small>';
};


#parameters again
my $viewingAlone = $wuID==$nID;	#true if viewing WU by itself, false if possibly with others (in an e2node)


my $inHeader;	#true=before WU text, false=after WU text (note: we use a slightly bad way of determining if we're in the header or footer)
#FIXME rename 'specialDisplays' to something better
my $fnName;	#name of current function
#lookup table of user-entered function to actual function to run
#	by default, function return values are cached
#	a function may override the default caching by setting $specialDisplays->{$fnName} to the string that should be used for future calls instead
my $specialDisplays;
my $CACHE_NAME = 'cache_displayWriteupInfo_'.$wuID;
if( (exists $HTMLVARS{$CACHE_NAME}) && (defined $HTMLVARS{$CACHE_NAME}) ) {
	$inHeader=0;
	$specialDisplays = $HTMLVARS{$CACHE_NAME};

	#this next part is temporary - N-Wing - September 2004
	my $errMsg = 'Possible duplicate writeup detected! (This is not the fault of the writeup author.)';
	if($isCE) {
		$errMsg .= ' This appears to be a side effect of the Kurt/A deletion bug. It can be fixed manually by an admin, or maybe automatically later.';
	}
	return userWarn($errMsg);

} else {
	$inHeader=1;
	$specialDisplays = {
		'type'=>\&info_wutype,
		'kill'=>\&info_kill,
		'vote'=>\&info_vote,
		'cfull'=>\&info_c_full,
		'cshort'=>\&info_c_short,
		'dtcreate'=>\&info_dt_create,
		'author'=>\&info_author,
		'authoranon'=>\&info_author_anon,
		'print'=>\&info_print,
		'pseudoanon'=>\&info_author_pseudo,
		'typeauthorprint'=>\&info_typeauthor,
		'notnew'=>\&info_hidden,	#original name
		'hidden'=>\&info_hidden,	#name that makes more sense
		'length'=>\&info_length,
		'sendmsg'=>\&sendMessage,
	};
	$HTMLVARS{$CACHE_NAME} = $specialDisplays;
}



#determine things to display
my @showThings;
if($inPrintMode) {
	#override user setting and show all information when printing
	if($inHeader) {
		@showThings = ('l:typeauthorprint','c:kill','c:vote','r:dtcreate','\n','l:cfull','c:hidden','r:length');
	} else {
		@showThings = ();
	}
} else {
	#use user vars, if set, or default
	if($inHeader) {
		#header
		if( (exists $$VARS{wuhead}) && (defined $$VARS{wuhead}) && length($$VARS{wuhead}) ) {
			@showThings = split(/\s*,\s*/, $$VARS{wuhead});	#split from HTML.pm - htmlcode
		} else {
			#no settings given, so use default header, which is mostly "classic"
			@showThings = ('l:typeauthorprint');
			push(@showThings, 'l:sendmsg') if $$VARS{NewbieBlab};
			push @showThings,('c:kill','c:vote');
			if($wuID==$nID) {
				push @showThings, ('r:dtcreate','\n','l:cfull');
			} else {
				push @showThings, ('c:cshort','r:dtcreate');
			}
		}
	} else {
		#footer
		if( (exists $$VARS{wufoot}) && (defined $$VARS{wufoot}) && length($$VARS{wufoot}) ) {
			@showThings = split(/\s*,\s*/, $$VARS{wufoot});	#split from HTML.pm - htmlcode
		} else {
			@showThings = ();
		}
	}
}
{
	#my $max = 16; #don't let user go nuts
	my $max = 50;	#don't let the user go too nuts (we cache now, so repeating something several times doesn't really matter)
	@showThings = @showThings[0..$max-1] if scalar(@showThings)>$max;
}

my $t;	#temporary values that subs can use


#display constants
my %tDataOpen = (
	'l'=>'<td>',
	'c'=>'<td align="center">',
	'r'=>'<td align="right">',
);
my $tDataClose = '</td>';
my $tRowOpen = '<table border="0" cellpadding="0" cellspacing="0" width="100%"><tr>';
my $tRowClose = "</tr></table>\n";



#
# subs
#

#update: only used 1 place, so rather pointless - N-Wing
##test for WU being votable or C!able
#my $mayVote=undef;
local *isVotable = sub {
	return 1;
#	return $mayVote if defined $mayVote;	#already known, use cached value
#	my $unVotable = getVars(getNode('boring heaven nodes','setting'));
#	$t = $$WRITEUP{title};
#	$t =~ s/\s/_/g;
#	$t =~ s/_\(.*?\)^//;	#get rid of " (thing)"
#	$mayVote = (exists $$unVotable{$t}) && $$unVotable{$t};
#	return $mayVote;
};



#links to the current WU, showing the given text
#does NOT create softlink, since they are useless between e2node and writeup
local *linkWU = sub {
	my ($txt,$rExtra) = @_;
	$txt = $WRITEUP->{title} || '???' unless (defined $txt) && length($txt);
	my %linkParams;
	%linkParams = %{$rExtra} if defined $rExtra;
	$linkParams{node_id}=$wuID;
	return '<a href='.urlGen(\%linkParams).'>'.$txt.'</a>';
};

#ripped from displayUserInfo
local *info_authorsince = sub {

	#not if bot, or printable, or $VARS
	return if $authorIsBot;
	return if $inPrintMode;
	return if $$VARS{info_authorsince_off};

	return unless $wuAuthor;

	return '<small>('.htmlcode('timesince',($wuAuthor->{lasttime}).',1').')</small>';
};

local *info_wutype = sub {
	$t = $$WRITEUP{wrtype_writeuptype} || 0;
	return userWarn(linkWU('bad WU type: 0')) unless $t;
	getRef $t;
	return userWarn(linkWU('bad WU type: '.$$t{node_id})) unless $t;
	return userWarn(linkWU('0 length WU type title: '.$$t{node_id})) unless length($$t{title});
	return '('.linkWU($$t{title}).')';
};

local *info_kill = sub {
	$specialDisplays->{$fnName} = '';
	return htmlcode('voteit',$wuID.',1');
};

local *info_vote = sub {
	return unless isVotable();
	$t = htmlcode('voteit',$wuID.',2');
	return unless((defined $t) && length($t));
	$specialDisplays->{$fnName} = '';
	return '<span style="white-space: nowrap" nowrap="nowrap">'.$t.'</span>';
};

local *info_c_full = sub {
	return htmlcode('writeupcools',$wuID);
};

local *info_c_short = sub {
	return info_c_full() if $inPrintMode;	#always show full info when printing
	return htmlcode('CoolUncoolIt',$wuID);
};

local *info_dt_create = sub {
	return '<small>'.htmlcode('parsetimestamp', $$WRITEUP{createtime}).'</small>';
};

local *info_author = sub {
	if(defined $wuAuthor) {
		my $authorLink = linkNode($wuAuthor,0,{lastnode_id=>0});
		$authorLink = '<s>'.$authorLink.'</s>' if $isCE && !$authorIsBot && (exists $wuAuthor->{acctlock}) && ($wuAuthor->{acctlock});
		return 'by <strong>' . $authorLink . '</strong> ' . info_authorsince();
	} else {
		return '<em>unable to find author '.$aid.'</em>';
	}
};

local *info_author_anon = sub {
	return (($isMine || $authorIsBot || hasVoted($WRITEUP, $USER)) ? &info_author() : '(anonymous)') . ($isCE?' '.info_authorsince():'');
};

local *info_author_pseudo = sub {
	return &info_author() if $isMine || $authorIsBot || hasVoted($WRITEUP, $USER);
	my $AT = getNodeById($aid);
	return '(very anonymous)' unless $AT;
	$AT = $$AT{title};
	return ' (<a href='.urlGen( $AT ? ({node=>$AT, type=>'user'}) : ({node_id=>$aid}) ).' title="'.$AT.'">anonymous</a>)'; #try hard to ensure link will work
};

local *info_print = sub {
	return if $inPrintMode;
	return '('.linkNode($WRITEUP, 'print', {displaytype=>'printable',lastnode_id=>0}).')';
};

local *info_typeauthor = sub {
	return &info_wutype() . ' ' . &info_author() . ' ' . &info_print();
};

local *info_hidden = sub {
	return unless $isCE || $isMine;
	my $disp = '<small>(created ' . ($$WRITEUP{notnew} ? 'hidden' : 'publicly') . ')</small>';
	$specialDisplays->{'notnew'} = $specialDisplays->{'hidden'} = $disp;
	return $disp;
};

local *info_length = sub {
	#most of these counts are rough,
	#and can be fooled rather easily;
	#however, it isn't worth taking
	#the CPU time to find exact values

	my $wdt = $$WRITEUP{doctext} || '';
	my $c;	#count

	#paragraphs - could be off by one if <p> incorrectly used to end a paragraph instead of to start one
	$c=0;
	while($wdt =~ /<[Pp][>\s]/g) {	#weak paragraph count
		++$c;
	}
	$c=1 if !$c;
	$t = $c.' <abbr title="approximate paragraphs">' . ($VARS->{noCharEntities} ? 'p' : '&para;') . '</abbr>, ';

	#if we want to burn CPU, we could count sections - p, blockquote, ul, ol, hr, anything else - as separators

	#now only deal with plain text
	$wdt =~ s/\<.+?\>//gs;

	#sentences
	$c = ($wdt =~ tr/.!?//);
	$t .= $c.' <abbr title="approximate sentences">s</abbr>, ' if $c;

	#words
	$c=0;
	while($wdt =~ /\w+/g) {
		++$c;
	}
	$t .= $c.' <abbr title="approximate words">w</abbr>, ' if $c;
#	$t .= (($wdt =~ s/\W+/ /gs)||0).' w, ';

	$t .= length($$WRITEUP{doctext}) . ' <abbr title="characters">c</abbr>';

	return $t;
};



#TODO? checkbox to have anonymous? maybe just for certain people?
local *sendMessage = sub {
	$specialDisplays->{$fnName} = '';
	return if $isGuest;
	my $qp='msgwuauthor_'.$wuID;	#query parameter
	my $t='';	#text to display - what was sent, error, etc.
	if(($aid!=$UID) && $$VARS{borged}) {
		$t = '(you may not talk now)';
	} elsif( (defined $query->param($qp)) && (length($query->param($qp)))  ) {
		my $msg = $query->param($qp);
		my $wut = $$WRITEUP{title};
		$wut =~ s/ \(\w+\)$//o;

		my $ccMe = (defined $query->param('cc'.$qp)) && ($query->param('cc'.$qp) eq '1') ? 1 : 0;

		$HTMLVARS{msgparams} = {
			'recipient_id'=>$aid,
			'message'=>$msg,
			'ccself'=>$ccMe,
			'renode'=>$wut,
#			'renode_id'=>$wuID,	#do this instead of 'renode' if and when message table gets its own field for this
		};
		my $failMessage = htmlcode('sendPrivateMessage','');
		$failMessage = undef unless (defined $failMessage) && (length($failMessage));
		if(defined $failMessage) {
			$t = ' <strong>Error</strong>: unable to send writeup message "'.$msg.'": '.$failMessage;
		} else {
			$query->param($qp,'');	#clear text field
			$t = $msg;
			$t =~ s/\</&lt;/g;
			$t =~ s/\>/&gt;/g;
			$t = ' <small>(sent writeup message "'.parseLinks($t,$NODE).'")</small>';
		}
	}
	return $query->checkbox('cc'.$qp,'','1','CC ').$query->textfield($qp,'',20,1500).$t;	#1530=255*6
};




#
# main
#

#build result
my $str = '';
my $s; #which Sub to call
my $r; #Result of sub call
my $align; #alignment
my $curRow = '';
#TODO allow multiple things in a table cell
foreach my $k (@showThings) {

	$fnName = $k;
	$align = $tDataOpen{l};
	if($fnName eq '\n') { #literal characters '\' and 'n', not newline
		if(length($curRow)) {
			$str .= $tRowOpen . $curRow . $tRowClose;
			$curRow = '';
		}
		next;

	} elsif($fnName =~ /^(.):(.+)$/) {
		#calling a function
		$align = $tDataOpen{$1} || $align;
		$fnName = $2;

#	} elsif($fnName =~ /^'(.+)'$/) {
#		#literal string
#		$fnName = encodeHTML($1);
#		$curRow .= $tDataOpen{l} . $fnName . $tDataClose if length($fnName);
#		next;

#	} elsif($fnName =~ /^"(.+)"$/) {
#		#mostly literal string
#		$fnName = encodeHTML($1);
#		$fnName =~ s/\\n/<br />/g;	//convert literal '\n' into an HTML newline
#		$curRow .= $tDataOpen{l} . $fnName . $tDataClose if length($fnName);
#		next;

	}
	next if length($fnName)==0;

	unless( (exists $specialDisplays->{$fnName}) && (defined $specialDisplays->{$fnName}) ) {
		$curRow .= $align.'<small>(unknown value: "'.encodeHTML($fnName).'"; see '.linkNodeTitle('Writeup Settings').')</small>'.$tDataClose unless $s;
		next;
	}

	$s = $specialDisplays->{$fnName};
	if( (ref $s) eq 'CODE' ) {
		#compute result
#$query->param('debug'.$wuID.$fnName.($inHeader?'head':'foot').int(rand(99)), 'uncached');
		$r = &$s();
		$r = '' if !defined $r;
		if( (defined $specialDisplays->{$fnName}) && (ref $specialDisplays->{$fnName}) eq 'CODE' ) {
			#the function is letting us handle caching
			$specialDisplays->{$fnName} = $r;
		}

	} else {
#$query->param('debug'.$wuID.$fnName.($inHeader?'head':'foot').int(rand(99)), 'cached');
		#use cached result
		$r = $s;

	}

	$curRow .= $align . $r . $tDataClose;

}
$str .= $tRowOpen . $curRow . $tRowClose if length($curRow);


if($inHeader) {
	#in-page ref to each WU
	$str = '<a name="author_'.$aid.'"><a name="node_id_'.$wuID.'">' . $str . '</a></a>';
} else {
	#not showing anything about this writeup anymore, so delete cache
	delete $HTMLVARS{$CACHE_NAME};
}


$str;
