node.code:
my ($N, $message) = @_;

$N ||= {};
my $type = $$N{type}{title} || 'draft';
my $new = !$$N{node_id};

my $str = '<fieldset><legend>';

if ($new){
	$str .= 'New draft';
	$str .= '/writeup' if $type eq 'writeup';
}else{
	$str .= 'Edit '.($$N{author_user} == $$USER{node_id} ? 'your ' : 'this ').$type;
}

$str .= '</legend>';

if ($type eq 'draft' and $$NODE{type}{title} ne 'e2node'){
	$str .= '<label>Title:'
		.$query -> textfield(
		-name => 'draft_title',
		class => 'draft_title',
		value => $$N{title},
		-force => 1,
		size => 80
	)
	.'</label><br>';
	$str .= '<small>You already have a draft or writeup called '
	.linkNode((getNodeWhere({title => $query -> param('draft_title'),
		author_user => $$N{author_user}}, 'draft'))[0] ||
		getNode($query -> param('draft_title'), 'e2node'))
	.'.</small>'
	if $query -> param('draft_title') &&
		cleanNodeName($query -> param('draft_title')) ne $$N{title};
}

$str .= qq'<textarea name="${type}_doctext" id="writeup_doctext" '
	.htmlcode('customtextarea', '1')
	.' class="formattable">'
	.encodeHTML($$N{doctext})
	.'</textarea>'
	.$message;

my $setType = "\n<p>".htmlcode('setwriteuptype', $$N{wrtype_writeuptype})."</p>"
	if $type eq 'writeup' && !$HTMLVARS{isMaintenance};

unless ($new){
	$str .= $setType.$query -> submit('sexisgood', "Update $type");
}else{
	$str .= '<input type="hidden" name="op" value="new">
	<p>';

	if ($type eq 'draft'){
		$str .= $query -> submit('sexisgood', 'Create draft')
			.'<input type="hidden" name="type" value="draft">
			</p>';
	}else{
		$str .= $query ->submit('sexisgood', 'submit')
		.' '.(
		$HTMLVARS{isMaintenance}
		? '(post immediately as maintenance writeup)'
			.$query -> hidden('type', 'writeup')
			.$query -> hidden('writeup_notnew', '1')
		: $query -> radio_group(
			-name => 'type',
			values => ['draft', 'writeup'],
			labels => {draft => 'post as draft', writeup => 'publish immediately'},
			default => $$VARS{defaultpostwriteup} ? 'writeup' : 'draft',
			force => 1
			)
		)
		.'</p>'
		.$setType;
	}
}

return "$str
	<p class='edithelp'><strong>Some Helpful Links:</strong>"
	.parseLinks('
		[E2 HTML Tags] &middot;
		[HTML Symbol Reference] &middot;
		[Using Unicode on E2] &middot;
		[Reference Desk]
		</p>
		</fieldset>');
