node.code:
my $str='[<i>Ajax call parameters, not original page parameters</i>]<br>' if $query->param('displaytype')eq 'ajaxupdate';
my $c=0;
my $q;
my $l;
my $isDebug;
foreach my $var ($query->param) {
	next if $var eq 'passwd';
	$q=$query->param($var);

	#Sanitise the variable for display
	$var =~ s/\</\&lt\;/g;
	$var =~ s/\>/\&gt\;/g;

	next if $q eq '';
	next if ($var eq 'op') && !$q;
	++$c;

	my $maxLen = 70;
	$isDebug = ($var =~ /^debug/i);
	$maxLen *= 2 if $isDebug;

	$str .= '<tt>' if $isDebug;
	$str .= '<strong>'.$var.'</strong>';
	if(($l=length($q))>$maxLen) {
		$str .= ':'.encodeHTML(substr($q,0,$maxLen),1).'... ('.$l.')';
	} elsif($q) {
		$str .= ':'.encodeHTML($q,1);
	}
	$str .= '</tt>' if $isDebug;
	$str .= "<br>\n";
}
$str = "<small>$c<br></small>\n".$str if $c;
$str;
