node.code:
#returns the given border size
#no borders (and hence sums) will be negative
#global variable used:
#	$$THEME{border_(LEVEL)_(DEPTH)}
#parameters:
#	required	$loc	which border - up, right, down, left; height (up + down); width (left + right); total (left+right+up+down)
#	optional	$level	which physical depth - defaults to "page"
#sub getBorderSize($;$) {
	my ($loc, $level) = @_;
	$level ||= 'page';
	my $val;
	if($loc eq 'width') {
		$val = htmlcode('getBorderSize', "left,$level") + htmlcode('getBorderSize', "right,$level");
	} elsif($loc eq 'height') {
		$val = htmlcode('getBorderSize', "up,$level") + htmlcode('getBorderSize', "down,$level");
	} elsif($loc eq 'total') {
		$val = htmlcode('getBorderSize', "left,$level") + htmlcode('getBorderSize', "right,$level") + htmlcode('getBorderSize', "up,$level") + htmlcode('getBorderSize', "down,$level");
	} else {
		$val = $$THEME{"border_${level}_size_$loc"} || 0;
	}
	$val = 0 if $val<0;
	return $val;
#}
