package XML::Parser::Lite;

use strict;
use vars qw($VERSION);
#$VERSION = sprintf("%d.%s", map {s/_//g; $_} q$Name:  $ =~ /-(\d+)_([\d_]+)/);
$VERSION = '0.65_3';

sub XML::Parser::Lite::new { 
  my $self = shift;
  my $class = ref($self) || $self;
  return $self if ref $self;

  $self = bless {} => $class;
  my %parameters = @_;
  $self->setHandlers(); # clear first 
  $self->setHandlers(%{$parameters{Handlers} || {}});
  return $self;
}

sub XML::Parser::Lite::setHandlers {
  my $self = shift; 
  no strict 'refs'; local $^W;
  # clear all handlers if called without parameters
  unless (@_) { foreach (qw(Start End Char Final Init)) { *$_ = sub {} } }
  while (@_) { my($name => $func) = splice(@_, 0, 2); *$name = defined $func ? $func : sub {} }
  return $self;
}

sub XML::Parser::Lite::regexp {
  my $patch = shift || '';
  my $package = __PACKAGE__;

  # This parser is based on "shallow parser" http://www.cs.sfu.ca/~cameron/REX.html 

  # Robert D. Cameron "REX: XML Shallow Parsing with Regular Expressions",
  # Technical Report TR 1998-17, School of Computing Science, Simon Fraser University, November, 1998.
  # Copyright (c) 1998, Robert D. Cameron. 
  # The following code may be freely used and distributed provided that
  # this copyright and citation notice remains intact and that modifications
  # or additions are clearly identified.

  my $TextSE = "[^<]+";
  my $UntilHyphen = "[^-]*-";
  my $Until2Hyphens = "$UntilHyphen(?:[^-]$UntilHyphen)*-";
  my $CommentCE = "$Until2Hyphens>?";
  my $UntilRSBs = "[^\\]]*](?:[^\\]]+])*]+";
  my $CDATA_CE = "$UntilRSBs(?:[^\\]>]$UntilRSBs)*>";
  my $S = "[ \\n\\t\\r]+";
  my $NameStrt = "[A-Za-z_:]|[^\\x00-\\x7F]";
  my $NameChar = "[A-Za-z0-9_:.-]|[^\\x00-\\x7F]";
  my $Name = "(?:$NameStrt)(?:$NameChar)*";
  my $QuoteSE = "\"[^\"]*\"|'[^']*'";
  my $DT_IdentSE = "$S$Name(?:$S(?:$Name|$QuoteSE))*";
  my $MarkupDeclCE = "(?:[^\\]\"'><]+|$QuoteSE)*>";
  my $S1 = "[\\n\\r\\t ]";
  my $UntilQMs = "[^?]*\\?+";
  my $PI_Tail = "\\?>|$S1$UntilQMs(?:[^>?]$UntilQMs)*>";
  my $DT_ItemSE = "<(?:!(?:--$Until2Hyphens>|[^-]$MarkupDeclCE)|\\?$Name(?:$PI_Tail))|%$Name;|$S";
  my $DocTypeCE = "$DT_IdentSE(?:$S)?(?:\\[(?:$DT_ItemSE)*](?:$S)?)?>?";
  my $DeclCE = "--(?:$CommentCE)?|\\[CDATA\\[(?:$CDATA_CE)?|DOCTYPE(?:$DocTypeCE)?";
  my $PI_CE = "$Name(?:$PI_Tail)?";

  # these expressions were modified for backtracking and events
  my $EndTagCE = "($Name)(?{${package}::end(\$2)})(?:$S)?>";
  my $AttValSE = "\"([^<\"]*)\"|'([^<']*)'";
  my $ElemTagCE = "($Name)(?:$S($Name)(?:$S)?=(?:$S)?(?:$AttValSE)(?{[\@{\$^R||[]},\$4=>defined\$5?\$5:\$6]}))*(?:$S)?(/)?>(?{${package}::start(\$3,\@{\$^R||[]})})(?{\${7} and ${package}::end(\$3)})";
  my $MarkupSPE = "<(?:!(?:$DeclCE)?|\\?(?:$PI_CE)?|/(?:$EndTagCE)?|(?:$ElemTagCE)?)";

  # Next expression is under "black magic".
  # Ideally it should be '($TextSE)(?{${package}::char(\$1)})|$MarkupSPE',
  # but it doesn't work under Perl 5.005 and only magic with
  # (?:....)?? solved the problem. 
  # I would appreciate if someone let me know what is the right thing to do 
  # and what's the reason for all this magic. 
  # Seems like a problem related to (?:....)? rather than to ?{} feature.
  # Tests are in t/31-xmlparserlite.t if you decide to play with it.
  "(?:($TextSE)(?{${package}::char(\$1)}))$patch|$MarkupSPE";
}

sub XML::Parser::Lite::compile { local $^W; 
  # try regexp as it should be, apply patch if doesn't work
  foreach (regexp(), regexp('??')) {
    eval qq{sub parse_re { use re "eval"; 1 while \$_[0] =~ m{$_}go }; 1} or die;
    last if eval { parse_re('<foo>bar</foo>'); 1 }
  };

  *compile = sub {};
}

setHandlers();
compile();

sub XML::Parser::Lite::parse { 
  init(); 
  parse_re($_[1]);
  final(); 
}

my(@stack, $level);

sub XML::Parser::Lite::init { 
  @stack = (); $level = 0;
  Init(__PACKAGE__, @_);  
}

sub XML::Parser::Lite::final { 
  die "not properly closed tag '$stack[-1]'\n" if @stack;
  die "no element found\n" unless $level;
  Final(__PACKAGE__, @_) 
} 

sub XML::Parser::Lite::start { 
  die "multiple roots, wrong element '$_[0]'\n" if $level++ && !@stack;
  push(@stack, $_[0]);
  Start(__PACKAGE__, @_); 
}

sub XML::Parser::Lite::char { 
  Char(__PACKAGE__, $_[0]), return if @stack;

  # check for junk before or after element
  # can't use split or regexp due to limitations in ?{} implementation, 
  # will iterate with loop, but we'll do it no more than two times, so
  # it shouldn't affect performance
  for (my $i=0; $i < length $_[0]; $i++) {
    die "junk '$_[0]' @{[$level ? 'after' : 'before']} XML element\n"
      if index("\n\r\t ", substr($_[0],$i,1)) < 0; # or should '< $[' be there
  }
}

sub XML::Parser::Lite::end { 
  pop(@stack) eq $_[0] or die "mismatched tag '$_[0]'\n";
  End(__PACKAGE__, $_[0]);
}

package XML::RSS::Parser::Lite::Item;

use strict;

our $VERSION = '0.10';

sub XML::RSS::Parser::Lite::Item::new {
	my $class = shift;

	my $self = {
		'title', '',
		'url', '',
		'description', '',
		@_,
	};

	bless($self, $class);
	return $self;
}


sub XML::RSS::Parser::Lite::Item::set {
	my $self = shift;

	my %defs = (@_);
	foreach my $k (keys %defs) {
		$self->{$k} = $defs{$k};
	}
}

sub XML::RSS::Parser::Lite::Item::get {
	my $self = shift;

	my $want = shift;
	return $self->{$want};
}


package XML::RSS::Parser::Lite;

use strict;

our $VERSION = '0.10';

sub XML::RSS::Parser::Lite::new { 
	my $class = shift;

	my $parser = new XML::Parser::Lite;
	my $self = {
		parser		=> $parser,
		place		=> '',
		title		=> '',
		url		=> '',
		description	=> '',
		items		=> [],
		latest		=> new XML::RSS::Parser::Lite::Item,
	};

	$self->{parser}->setHandlers(
		Final	=> sub { shift; $self->final(@_) },
		Start	=> sub { shift; $self->start(@_) },
		End	=> sub { shift; $self->end(@_) },
		Char	=> sub { shift; $self->char(@_) },
	);

	bless($self, $class);
	return $self;
}

sub XML::RSS::Parser::Lite::parse {
	my $self = shift;
	my $xml = shift;

	$self->{parser}->parse($xml);
}


sub XML::RSS::Parser::Lite::final { 
	my $self = shift; 

	$self->{parser}->setHandlers(Final => undef, Start => undef, End => undef, Char => undef);
}

sub XML::RSS::Parser::Lite::start {
	my $self = shift;
	my $tag = shift;

	$self->{place} .= "/$tag";
	$self->{latest} = $self->add if ($self->{place} eq '/rss/channel/item');
}

sub XML::RSS::Parser::Lite::char {
	my $self = shift;
	my $text = shift;

	$self->{latest}->set('title', $text) if ($self->{place} eq '/rss/channel/item/title');
	$self->{latest}->set('url', $text) if ($self->{place} eq '/rss/channel/item/link');
	$self->{latest}->set('description', $text) if ($self->{place} eq '/rss/channel/item/description');

	$self->{title} = $text if ($self->{place} eq '/rss/channel/title');
	$self->{url} = $text if ($self->{place} eq '/rss/channel/link');
	$self->{description} = $text if ($self->{place} eq '/rss/channel/description');
}

sub XML::RSS::Parser::Lite::end { 
	my $self = shift; 
	my $tag = shift;

	my $place = $self->{place};
	$place = substr($place, 0, length($place)-length($tag)-1); # regex here causes segmentation fault!
	$self->{place} = $place;
}



sub XML::RSS::Parser::Lite::add {
	my ($self) = shift;

	my $it = new XML::RSS::Parser::Lite::Item(@_);
	push(@{$self->{items}}, $it);

	return $it;
}


sub XML::RSS::Parser::Lite::count {
	my $self = shift;
	return scalar @{$self->{items}};
}


sub XML::RSS::Parser::Lite::get {
	my $self = shift;

	my $what = shift;
	if ($what =~ /^\d*$/) {
		return @{$self->{items}}[$what];
	}

	return $self->{$what};
}

require LWP::Simple;
        my $xml =  LWP::Simple::get("http://news.bbc.co.uk/rss/on_this_day/front_page/rss.xml");
        my $rp = new XML::RSS::Parser::Lite;
        $rp->parse($xml);

        my $str;

        for (my $i = 0; $i < $rp->count(); $i++) {
                my $it = $rp->get($i);
                $str .= "<p><b>" . $it->get('title') . "</b><br>" . $it->get('description') . "</p>\n";
        }
$str;
