node.code:
my ($healID, $inChat) = @_;

use JSON;

my $friendList = '';
if (htmlcode('isInUsergroup',$$USER{user_id}.',heroes')) {
	$friendList = "heroes";
}
if (htmlcode('isInUsergroup',$$USER{user_id}.',villains')) {
	$friendList = "villains";
}

return unless $friendList;

#return "/msg kthejoker $enemyID" if ($$USER{title} eq 'kthejoker');

my $randVillain;
$randVillain = getNodeById($healID);
return unless htmlcode('isInUsergroup',$$randVillain{user_id}.",".$friendList);

return unless $randVillain;

my $vVars =getVars($randVillain);
my $vWarVars = $vVars->{thewar};
$vWarVars = from_json($vWarVars);
$vWarVars->{health} += 20;
$vVars->{thewar} = to_json($vWarVars);
setVars($randVillain, $vVars);

my $scalar = from_json($$VARS{thewar});
return if (time - $scalar->{laststrike} <= 4);
return if $scalar->{fallen};

$scalar->{laststrike} = time;
$$VARS{thewar} = to_json($scalar);
setVars($USER,$VARS);





my $warStr = to_json($scalar);
$$VARS{thewar} = $warStr;

if ($inChat) {
my $message = "/me heals ".$$randVillain{title}.".";
$message .= "  ".$$randVillain{title} ."'s health: ".$vWarVars->{health} if ($vWarVars->{health} > 0);
return $message;
}
