node.code:
my ($limit, $log_id,$remlabel, $hideLinker, $skipFilterHTML, $listOnly) = @_;
$limit ||= 5;
$log_id ||= getId($NODE);
$remlabel||='remove';
$skipFilterHTML = ($skipFilterHTML eq '1') ? 1 : 0;
$listOnly = ($listOnly eq '1') ? 1 : 0;

my $skip = 0;
my $interval = $limit;
my $uid = getId($USER);
my $isRoot = exists $HTMLVARS{group_gods}{$uid};
my $isCE = $isRoot || exists $HTMLVARS{'group_content editors'}{$uid};
my $colspan = 2;
# usergroup owner
my $isOwner = 0;
$isOwner = 1 if ($$USER{node_id} == getVars(getNode('usergroup owners','setting'))->{$$NODE{node_id}});
my $canRemove = $isRoot || $isOwner;

#0=show (default), 1=CE can see, 2=root can see, 3=nobody can see
#	not defined -> 0
#	1 or 2 or 3 -> 1 or 2 or 3
#	illegal -> 3
if(!defined $hideLinker) {
	$hideLinker = 0;
} elsif(($hideLinker =~ /^(\d)$/) && (($hideLinker=$1)<=3)) {
} else {
	$hideLinker = 3;
}

--$hideLinker if $isCE;
if($canRemove) {
	--$hideLinker;
	++$colspan; #remove link
}
$hideLinker = 0 if $hideLinker < 0;
++$colspan unless $hideLinker;

if (defined $query and defined $query->param('nextweblog')) {
	my $nwl = $query->param('nextweblog');
	$skip = $nwl - $interval;
	$limit = $nwl;  
}

my $VALIDTAGS;
#if we're screening, get the allowed HTML tags
unless($skipFilterHTML) {
	my $TAGNODE = getNode('approved html tags','setting');
	if($TAGNODE) {
		$VALIDTAGS = getVars($TAGNODE);
	} else {
		#want to screen, but can't find tags; so ????
		#TODO? just not allow any tags?
	}
}
my $drafttype = getType('draft') -> {node_id};

my $csr = $DB->sqlSelectMany('*', 
	'weblog, node',
		"weblog_id=$log_id AND
		removedby_user=0 AND
		to_node = node_id AND
		node.type_nodetype != $drafttype",
	'ORDER BY linkedtime DESC');

my $count = 0;
my $str;
my $zenStr;

if ($listOnly) {
$str = "<ul>";

while(my $L = $csr->fetchrow_hashref) {

	$count++;
	next unless $skip < $count;
	last if $count > $limit;

	my $N = getNodeById($$L{to_node});
	$str.='<li>' . linkNode($N,'',{lastnode_id =>0}).' by ' .linkNode($$N{author_user},'',{lastnode_id =>0}) . "</li>";
}


$str.="</ul>";
$zenStr = $str; }

else {

$str.= '<table width="100%" cellpadding="0" cellspacing="1">';


my $text;
while(my $L = $csr->fetchrow_hashref) {

	$count++;
	next unless $skip < $count;
	last if $count > $limit;

	my $N = getNodeById($$L{to_node});
	my $isWriteup = (($$N{type}{title} eq 'writeup')||($$N{type}{title} eq 'e2node'));
	my ($date) = $$N{createtime};
	$str.='<tr bgcolor="'.$$THEME{writeupTitleColor}."\">\n";
	# More class=oddrow quickie-fixies pending proper themeprefs. -- wharf
	$str.='<td class="oddrow" width="70%">' . linkNode($N,'',{lastnode_id =>0})."</td>\n";
	$str.='<td class="oddrow">by ' .linkNode($$N{author_user},'',{lastnode_id =>0}) . "<br />($date)" . "</td>\n";
	$str .= '<td class="oddrow">'.($$L{linkedby_user}==$$N{author_user} ? '&nbsp;' : ('linked by '.linkNode($$L{linkedby_user},'',{lastnode_id =>0})))."</td>\n" unless $hideLinker;

	$str.="<td class=\"oddrow\"><a href=" . urlGen({node_id => $log_id, source => $log_id, to_node => getId($N), op => 'removeweblog', 'removedby_user' => getId($USER)}) . ">$remlabel</a></td>\n" if $canRemove;

	$str.="</tr>\n";
	$text = $$N{doctext};
	unless(($skipFilterHTML)||(!$isWriteup)) {
		$text = htmlScreen($text,$VALIDTAGS);
	}
	if ($isWriteup) {
		$text=parseLinks($text);
	}
	else {
		$text = parseCode($text);
	}
	$str.='<tr><td colspan="'.$colspan.'">'.$text.'</td></tr>';
	$str .= '<tr><td bgcolor="#000000" colspan="'.$colspan.'">'.htmlcode('clearimage','2,1').'</td></tr>';


#zen Alternative
$zenStr .="<div class='weblog_item'>";
$zenStr.="<div class='contentinfo'>
<h3 class='title wl_title'>".linkNode($N,'',{lastnode_id =>0})."</h3>
<div class='wl_author'>".linkNode($$N{author_user},'',{lastnode_id =>0, -class=>'author'})."</div>
<div class='date wl_date'>".$date."</div>";

$zenStr .= "<div class='wl_linkedby'>".($$L{linkedby_user}==$$N{author_user} ? '&nbsp;' : ('linked by '.linkNode($$L{linkedby_user},'',{lastnode_id =>0})))."</div>" unless $hideLinker;
$zenStr.="<div class='wl_remove'><a href=" . urlGen({node_id => $log_id, source => $log_id, to_node => getId($N), op => 'removeweblog', 'removedby_user' => getId($USER)}) . ">$remlabel</a></div>" if $canRemove;

$zenStr.="</div>
<div class='content wl_text'>".$text."</div>";
$zenStr.="</div>";




}

if($skip or $count > $limit) {
my $linkStr = '';
	$str.='<tr><td colspan="'.$colspan.'"><p align="right">';
	$linkStr.= linkNode($NODE, '<- newer', {nextweblog => $limit - $interval}) if $skip;
	$linkStr.= ' | ' if $skip and $count > $limit;
	if(getId($NODE) == $log_id) {
		$linkStr.= linkNode($NODE, 'older ->', {nextweblog => $limit + $interval}) if $count > $limit;
	} else {
		$linkStr.= linkNode($log_id, 'older ->') if $count > $limit;
	} 
	$str = $str.$linkStr.'</td></tr>';
	$zenStr .= "<div class='wl_links'>".$linkStr."</div>";


}
$str.='</table>';

}

if (htmlcode('isZen')) {
return $zenStr;
}

$str;
