node.code:
my ($limit) = @_;

$limit ||= 50;

my $qry = 'SELECT node_id FROM newwriteup';

my $uid = getId($USER);
my $isEd = (exists $HTMLVARS{'group_content editors'}{$uid}) || (exists $HTMLVARS{'group_gods'}{$uid});

my $nr = getId(getNode('node row', 'superdoc'));

$qry.= ' WHERE notnew=0 ' unless $isEd;
$qry.= " ORDER BY newwriteup_id DESC LIMIT $limit";

my $ed = 'ed,' if $isEd;
my $funk = sub{
		my $N = shift;
		my $kuid = $DB->sqlSelect('linkedby_user', 'weblog', "weblog_id=$nr and to_node=$$N{node_id}") || 0;
		my $str.='<td>';

		if($$N{notnew}){
		  $str .= '(<font color="red">H!</font>)';
                $str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'unhidewriteup', 'hidewriteup'=>$$N{node_id}}).'>un-h!</a>)';
                }else{
		$str .= '(<a href='.urlGen({'node_id'=>$$NODE{node_id}, 'op'=>'hidewriteup', 'hidewriteup'=>$$N{node_id}}).'>h?</a>)';
                }
		$str .= '(<font color="red">...</font>)' if $DB->sqlSelect('notnew', 'newwriteup', "node_id=$$N{node_id}") != $$N{notnew};

		$str.='</td><td>';
		$str.='X' if $kuid;
		$str.='&nbsp;</td>';
		return $str;
};

my $nids = $dbh -> selectcol_arrayref($qry);

htmlcode('show content', $nids ,
	qq'<tr class="&oddrow">$ed "<td>", parenttitle, type, "</td><td>", listdate, "</td><td>", author, "</td>"', 'ed' => $funk);
