node.code:
my ($src_id, $name, $group) = @_;

return unless $$NODE{type}{sqltablelist} =~ /document/;
return if $$NODE{node_id}==$$USER{node_id};	#stop newbies from *ifying their homenode, darnit!

$name ||= 'Newsify';

#see if user may add something to weblog
unless(exists $HTMLVARS{group_gods}{getId($USER)}) {
	return unless $group;
	my $G = getNode($group,'usergroup');
	return unless (defined $G) && (grep /^$$USER{node_id}$/, @{ $$G{group} });
}

return if $DB->sqlSelect('weblog_id', 'weblog', 'weblog_id='.int($src_id).' AND to_node='.getId($NODE));	#see if already on current log

my $title= $$NODE{title};
$title =~s/[\"]//gs;

my $str = "<script language='javascript'>\n";
$str .= "function confirm$name() {\n";
$str .= "  if(confirm(\"Really $name '$title'?\"))\n";
$str .= '  { parent.location=';
$str .= urlGen({ node_id => getId($NODE), source => $src_id,
   target => getId($NODE), op => 'weblog'});
$str .= ";}\n";
$str .= "}\n</script>\n";

$str .= "<a href=\"javascript:confirm$name();\">$name!</a> <a href=".urlGen({node_id=>$src_id}).">(view)</a>\n";
$str;
