node.code:
return "" unless exists($HTMLVARS{'group_gods'}{$$USER{node_id}}) or exists($HTMLVARS{'group_content editors'}{$$USER{node_id}});

my @choices;

if($NODE->{orderlock_user}){

push @choices, "<i>Writeup ordering locked by ".linkNode(getNodeById($NODE->{orderlock_user}))."</i>";

}else{


if(exists($HTMLVARS{'group_gods'}{$$USER{node_id}})){
 push @choices, htmlcode("windowview", "editor,launch editor");

}

  push @choices, linkNode($NODE, "repair and reorder", 
     {'repair_id' => $$NODE{node_id}, 'op' => 'repair_e2node'});
}
  push @choices, linkNode($NODE, "repair only", 
     {'repair_id' => $$NODE{node_id}, 'op' => 'repair_e2node', 'noorder' => 1});


return "(<small> ".join(" | ", @choices)." </small>)<br>";
