# parameters fed in through $HTMLVARS{msgparams} ('old way') or in first argument
# in old way, first argument may contain flags

my $params = shift;
my $flags;

if (ref $params){
	$flags = $$params{flags};
}else{
	$flags = $params;
	$params = $HTMLVARS{msgparams};
	return 'No message parameters.' unless ref $params;

}

my $keepParams = $flags & 1;
