node.code:
my ($N, $title) = @_;

my $type;
if (ref $N){
	$type = $$N{wrtype_writeuptype};
}else{
	$type = $N;
}

my $hidebox;

unless ($type){
	# no old type: new writeup/draft for publication
	my $checked;

	$title ||= $$NODE{title};
	return $query -> hidden('writeup_notnew', 1)
		.'Writeup type: thing; don\'t show in New Writeups nodelet'
		if getVars(getNode('boring heaven nodes', 'setting')) -> {$title};

	if (
		$title =~ /^(January|February|March|April|May|June|July|August|September|October|November|December) [1-9]\d?, \d+/i ||
		$title =~/^(dream|editor|root) Log: /i ||
		$$VARS{HideNewWriteups}
	){
		$type = getNode('log', 'writeuptype')->{node_id} if $1;
		$checked = 1;
	}

	$checked = ' checked="checked"' if $checked
		or ref($N) && 
		($$N{reputation} || $DB -> sqlSelect('vote_id', 'vote', "vote_id=$$N{node_id}")
		|| $DB -> sqlSelect(
			'LEFT(notetext, 25)'
			, 'nodenote'
			, "nodenote_nodeid=$$N{node_id}
				AND noter_user = 0"
			, 'ORDER BY timestamp LIMIT 1'
		) eq 'Restored from Node Heaven');

	$type ||= getNode('thing', 'writeuptype') -> {node_id};

	$hidebox = qq!<label><input type="checkbox" name="writeup_notnew" value="1"$checked>don't
		show in New Writeups nodelet</label>!;
}

my $str = '<label title="Set the general category of your writeup,
	which helps identify the type of content in writeup listings."><strong>Writeup type:</strong>';

my (@WRTYPE) = getNodeWhere({type_nodetype => getId(getType('writeuptype'))});

my %items;

my $isEd = exists $HTMLVARS{'group_content editors'}{getId($USER)} ||
	exists $HTMLVARS{'group_gods'}{getId($USER)} ||
	$$USER{title} eq 'Webster 1913' ||
	$$USER{title} eq 'Virgil';

foreach (@WRTYPE){
	next if (!$isEd and lc($$_{title}) eq 'definition'
		|| lc($$_{title}) eq 'lede');
	$items{$$_{node_id}} = $$_{title};
}

$items{$type} = '('.$type.')' if !defined($items{$type});
my @idlist = sort { $items{$a} cmp $items{$b} } keys %items;

$str.=$query->popup_menu('writeup_wrtype_writeuptype', 
  \@idlist, $type, \%items) . '</label>';

$str . $hidebox;
