node.code:
no strict;
use Everything::XML;
my @modules = (
	'Everything',
	'Everything::XML',
	'Everything::NodeBase',
	'Everything::Experience',
	'Everything::HTML'
);
my $str;
my $str2;

my $showHTMLCODE = $$VARS{scf_nohtmlcode} ? 0 : 1;	#must be 0 or 1

if(not $query->param('choicefunc')) {
	my %funcs;
	my $rows = 0;
	$str .= '<table><tr>';

	my $colwidth = int (100/(int(@modules)+$showHTMLCODE)) .'%';
	foreach my $modname (@modules) {
		local *stash;
		*stash = *{ "${modname}::" };
		my @modfuncs;
		foreach(keys %stash) {
			push (@modfuncs, $_) if (defined &{ $stash{$_} } and ($modname eq 'Everything' or not exists $Everything::{$_})) ;
#			push (@modfuncs, $_) if (defined &{ $stash{$_} }) ;
#			push (@modfuncs, $_) if (($modname eq 'Everything' or not exists $Everything::{$_})) ;

#			$str2 .= "<p>$modname &diams; $_ &diams;" . $stash{$_} . "</p>";

		}
		@modfuncs = sort {$a cmp $b} @modfuncs;
		$funcs{$modname} = \@modfuncs;
		$rows = int(@modfuncs) if $rows < int(@modfuncs);
		$str.='<th width="'.$colwidth.'">'.$modname.'</th>';
#		$str.='<td width="'.$colwidth.'">'.$modname.'</td>'; ## suggested test fix
	}

	if($showHTMLCODE) {
		$str.="<th width=\"$colwidth\">HTMLCODE</th>\n";
		my @HTMLCODE = $DB->getNodeWhere({1=>1}, $DB->getType('htmlcode'), 'title ASC', 'light');
#		@HTMLCODE = sort {$a cmp $b} @HTMLCODE;
		$funcs{htmlcode}= \@HTMLCODE;
		$rows=int(@HTMLCODE) if $rows < @HTMLCODE;
	}

	foreach (sort(keys(%{$Everything::})))
#	{ $str2 .= "<p>" . ${$Everything::}{$_} . "</p>" ; }
	{ $str2 .= "<p>" . "test" . "</p>" ; }

	$str .= "</tr>\n";

	my $count=0;
	while($count < $rows) {
		$str.='<tr>';
		foreach(@modules) {
			$str.= '<td>';
			$str.=linkNode($NODE, $funcs{$_}[$count], {
				choicefunc => $funcs{$_}[$count],
				lastnode_id=>0
			}) if (int (@{ $funcs{$_} }) > $count);
			$str.='</td>';
		}
		$str.='<td>';
		$str.= linkNode($funcs{htmlcode}[$count]) if $count < @{ $funcs{htmlcode} };
		$str.="</td></tr>\n";
		$count++;
	}  

	return $str2 . $str . '</table>';
}

#else, we have have a specific function to display
$str.= 'or go back to the code '.linkNode($NODE, 'index');
my $choicefunc = $query->param('choicefunc');
my $parentmod = '';

foreach my $modname (@modules) {
	next if $parentmod;
	local *stash;
	*stash = *{ "${modname}::" };
	if (exists $stash{$choicefunc}) {
		$parentmod=$modname;
	}
}
unless($parentmod) {
	return "<em>Sorry, man.  No dice on $choicefunc</em>.<br />\n"; 
}


my $parentfile;
my @mod;

foreach (@INC)
{
   $parentfile = "$_\/".$parentmod.".pm";
   $parentfile =~ s/\:\:/\//g;
   open MODULE, $parentfile or next;
   @mod = <MODULE>;
   close MODULE;
   last;
}


if (@mod) {
	$str.= "module $parentmod loaded: ".int(@mod)." lines\n";
} else {
	$str.= "hmm. couldn't load modules $parentfile\n";
}

my $count = 0;
my @lines;
my $fullText = '';
while(@mod > $count and not @lines) {
	$fullText .= $mod[$count];
	if($mod[$count] =~ /^sub $choicefunc\s*/) {

		my $i = $count;
		my $flag;
		do {
			$i--;
			$mod[$i]=~/\s*(\S)/;

			$flag = (not defined $1 or $1 eq '#');
		} while($i > 0 and $flag);
		do {
			$i++;
			push @lines, $mod[$i];
		} while (not ($mod[$i] =~ /^\}\s*$/ ));

	}
	$count++;
}

if (@lines) {
	$str.= listCode(join('', @lines));
} else {
	$str = listCode($fullText);
}
$str;
