node.code:
my ($POLL, $showStatus) = @_;
getRef $POLL;
$POLL ||= $NODE;

return "Ack! Can't find poll!" unless $POLL && $$POLL{type}{title} eq 'e2poll';

return 'Poll update conversion failed' if $$POLL{doctext} =~ /\[%/
	and htmlcode('convertOldPoll', $POLL) != 1;

my $vote = ($DB->sqlSelect(
	'choice'
	, 'pollvote'
	, "voter_user=$$USER{node_id} AND pollvote_id=$$POLL{node_id}"
))[0];

$showStatus = " ($$POLL{poll_status})" if $showStatus;
my $str = '<h2>'.linkNode($POLL)."$showStatus</h2>"
	unless $$POLL{node_id} == $$NODE{node_id};

$str .= '<p><cite>by '
	.linkNode($$POLL{poll_author}, '', {-class => 'author'})
	.'</cite></p>
	<h3>'
	.$query -> escapeHTML($$POLL{question}) # question is unsanitised user input...
	.'</h3>';

my @options = split /\s*\n\s*/s, parseLinks($$POLL{doctext});

unless (defined $vote or $$POLL{poll_status} eq 'closed'){
	my @values = ();
	@options = map {(($values[scalar @values] = scalar @values) => $_)} @options;
	my $buttons = $query->radio_group(
		-name => 'vote',
		-values => \@values,
		-linebreak=>"true",
		-labels => {@options}
	);
	unless ($$POLL{poll_status} eq 'new'){
		$str .= htmlcode('openform', -class => 'e2poll')
			.qq'<input type="hidden" name="op" value="pollvote">
			<input type="hidden" name="poll_id" value="$$POLL{node_id}">'
			.$buttons
			.htmlcode('closeform', 'vote');
	}else{ # new poll is inactive
		$str .= '<form class="e2poll">'
			.$buttons
			.'</form>'
	}
}else{
	my @results = split ',', $$POLL{e2poll_results};
	my $votedivider = $$POLL{totalvotes}||1;
	$str .= '<table class="e2poll">';
	my $i = 0;
	while($options[$i]){
		$str.='<tr>
		<td>'.($i == $vote ? '<b>' : '').$options[$i].($i ==$vote ? '</b>' : '').'</td>
		<td>'.$results[$i].'</td>
		<td>'.sprintf("%2.2f",($results[$i]/$votedivider)*100).'%</td>
		</tr>';
		$str.="<tr><td colspan='3'><img class='oddrow' src='/images/incoming/dot.gif'
			height='8' width='"
			.sprintf("%2.0f",($results[$i]/$votedivider)*180)."' /></td></tr>";
		$i++;
	}
	$str.='<tr>
	<td><b>Total</b></td>
	<td>'.$$POLL{totalvotes}.'</td>
	<td>'.sprintf("%2.2f",100).'%</td>
	</tr>';
	$str.='</table>';
}

return $str;
