node.code:
my ($enemyID, $inChat) = @_;

use JSON;



my $enemyList = '';
if (htmlcode('isInUsergroup',$$USER{user_id}.',heroes')) {
	$enemyList = "villains";
}
if (htmlcode('isInUsergroup',$$USER{user_id}.',villains')) {
	$enemyList = "heroes";
}

return unless $enemyList;

#return "/msg kthejoker $enemyID" if ($$USER{title} eq 'kthejoker');

my $randVillain;
if ($enemyID != -1) {
	$randVillain = getNodeById($enemyID);
	return unless htmlcode('isInUsergroup',$$randVillain{user_id}.",".$enemyList);
}
else {
	my @enemies = @{getNode($enemyList,'usergroup')->{group}};
	my $arrcount = @enemies;
	$arrcount--;
	$randVillain = getNodeById($enemies[int(rand($arrcount))]);
}

return unless $randVillain;

my $vVars =getVars($randVillain);
my $vWarVars = $vVars->{thewar};
if (!$vWarVars) {
	$vWarVars = '{ "health": 150}';
}
$vWarVars = from_json($vWarVars);

if (!$$VARS{thewar}) {
	$$VARS{thewar} = '{"health": 150}';
}

my $scalar = from_json($$VARS{thewar});
return if (time - $scalar->{laststrike} <= 4);
return if $scalar->{fallen};

$scalar->{strike} += 1;
$scalar->{laststrike} = time;
$$VARS{thewar} = to_json($scalar);
setVars($USER,$VARS);

my $bonus = 0;
for my $key ( keys %{$scalar->{inventory}} ) {
	$bonus += $scalar->{inventory}->{$key}->{damageBonus};	
}

for my $key ( keys %{$vWarVars->{inventory}} ) {
	$bonus -= $vWarVars->{inventory}->{$key}->{shieldBonus};	
}

my $damage = int(rand(10))+getLevel($USER) + int($scalar->{kills} / 9) + $bonus;
my $isCritical = (int(rand(99)) < 2);
$damage = $damage * 2 if $isCritical;
$scalar->{damageDone} += $damage;
$vWarVars->{health} -= $damage;
if ($vWarVars->{health} <= 0) {
	$vWarVars->{fallen} = 1;
	$scalar->{kills} += 1;
	if (!htmlcode('isInUsergroup',$$randVillain{user_id}.',valhalla')) {
		my $valhalla = getNode('valhalla','usergroup');
		insertIntoNodegroup($valhalla,-1,$randVillain);
		updateNode($valhalla,-1);
	}

	removeFromNodegroup(getNode($enemyList,'usergroup'),$randVillain,-1);
	updateNode(getNode($enemyList,'usergroup'),-1);

	for my $key ( keys %{$vWarVars->{inventory}} ) {
		$scalar->{inventory}->{$key} = $vWarVars->{inventory}->{$key};
	}

	$vWarVars->{inventory} = {};
}

$vVars->{thewar} = to_json($vWarVars);
setVars($randVillain, $vVars);

my $warStr = to_json($scalar);
$$VARS{thewar} = $warStr;

if ($inChat) {
my $message = "/me strikes ".$$randVillain{title} ." for $damage damage!";
$message .= " CRITICAL HIT!" if $isCritical;
$message .= "  ".$$randVillain{title} ." has fallen in battle!" if $vWarVars->{fallen};
$message .= "  ".$$randVillain{title} ."'s health: ".$vWarVars->{health} if ($vWarVars->{health} > 0);
return $message;
}
