node.code:
#voteit - pass writeup's node_id

my ($N,$showParts) = @_;

$N = $NODE unless $N;

getRef($N);
return unless $$N{type}{title} eq 'writeup';

$showParts ||= 3;# 1=show killing stuff, 2=show voting stuff

my $userID = getId($USER);
return if $userID == $HTMLVARS{default_user};
my $isEd = (exists $HTMLVARS{group_gods}{$userID}) || (exists $HTMLVARS{'group_content editors'}{$userID});
my $inPrintMode = $query->param('displaytype') eq 'printable'; #FIXME - is that the best way to see if printable?

my $nid = getId($N);
return '<font color="blue">(ERROR: unknown node_id in voteit)</font>' if !$nid;
my $nr = getId(getNode('node row', 'superdoc'));


my ($kuid, $unkuid) = $DB->sqlSelect('linkedby_user, removedby_user', 'weblog', "weblog_id=$nr and to_node=$nid");
$kuid ||= 0;
$unkuid ||= 0;


my $str='';

if (($showParts & 1) && ($isEd || $kuid)) {
  $str .= '(<small id="killheader' . $nid . '">';

  if ($isEd) {

    if ($kuid) {
      $str.=linkNode($nr, 'X').' by '.linkNode($kuid);
      $str .= ', restored by '.linkNode($unkuid) if $unkuid;
    }
    elsif (!$inPrintMode) {

      #		#N-Wing, Friday, May 24, 2002: only load insurance info once
      #		unless( (exists $HTMLVARS{cache_nukeinsurance}) && (defined $HTMLVARS{cache_nukeinsurance}) ) {
      #			$HTMLVARS{cache_nukeinsurance} = getVars(getNode('nuke insurance', 'setting'));
      #		}
      #		my $publish = $HTMLVARS{cache_nukeinsurance};

      my $publish = $DB->sqlSelect('publisher','publish',"publish_id = $nid limit 1") || 0;

      if ($publish) {
        $str .= 'insured by '.linkNode($publish).' ('.linkNode($NODE, 'undo', {op=>'insure', ins_id=>$nid}).')';
      }
      else {

        $str.=linkNode($NODE, 'Insure', {op=>'insure', ins_id=>$nid}).' ';
        $str .= $query->checkbox('killnode'.$nid, 0, 1, 'axe');
        $str .= $query->checkbox('instakill'.$nid, 0, 1, 'InstaKill');

        #maybe make this optional
        $str .= '<small>'.$query->textfield('killreason'.$nid, '', 12, 200).'</small>';

      }
    }
  }
  else {
    if ($kuid && !$unkuid) {
      $str .= 'marked for destruction';
    }
  }
  $str .= '</small>) ';
  $str = '' if $inPrintMode;  #FIXME have a $curSection thingy instead

}                               #killing section


return $str if ($$N{wrtype_writeuptype} eq getId(getNode('definition', 'writeuptype')));
return $str if htmlcode('flagged',$nid.',unvotable');

my $votestr;
if ($showParts & 2) {

  #no voting on maintenance e2nodes
  my $isMaintenance = 0;
  {
    my $parentID = $$N{parent_e2node};
    foreach (values %HTMLVARS) {
      if ($parentID eq $_) {
        #return $votestr;	#original way
        $isMaintenance=1;
        last;
      }
    }
  }

  use Everything::Experience;
  #

  #N-Wing, Tuesday, February 11, 2003 - cached vote label settings
  my $CACHE_NAME = 'cache_voteit_votesettings';
  unless ( (exists $HTMLVARS{$CACHE_NAME}) && (defined $HTMLVARS{$CACHE_NAME}) ) {
    $HTMLVARS{$CACHE_NAME} = getVars(getNode('vote settings','setting'));
  }

  my $VSETTINGS = $HTMLVARS{$CACHE_NAME};
  my $uplbl = $$VSETTINGS{upLabel} if exists $$VSETTINGS{upLabel};
  $uplbl ||= 'up';
  my $dnlbl = $$VSETTINGS{downLabel} if exists $$VSETTINGS{downLabel};
  $dnlbl ||= 'down';
  my $nolbl = $$VSETTINGS{nullLabel} if exists $$VSETTINGS{nullLabel};
  $nolbl ||= 'none';

  my $isMine = $userID==$$N{author_user};
  my $prevvote = $isMine ? 0 : $DB->sqlSelect('weight', 'vote', 'vote_id='.$$N{node_id}.' and voter_user='.$userID) || 0;
  if ( $isMine || $prevvote ) {

    #N-Wing, Sunday, June 23, 2002 - slightly changed appearance of vote info

    my $uv = $prevvote;
    my $r = $$N{reputation} || 0;

    #new way - slower, but shows things exactly as we know it
    my ($numP) = $DB->sqlSelect('count(*)', 'vote', "vote_id=$$N{node_id} AND weight>0");
    my ($numM) = $DB->sqlSelect('count(*)', 'vote', "vote_id=$$N{node_id} AND weight<0");

    my $p = $numP;
    my $m = $numM;

    #format voting info
    $r = '<strong>'.$r.'</strong>' if $query->param('vote__'.$nid);
    $p = '+'.$p;
    $m = '-'.$m;
    if ($uv>0) {
      $uv='+';
      $p = '<strong>'.$p.'</strong>';
    }
    elsif ($uv<0) {
      $uv='-';
      $m = '<strong>'.$m.'</strong>';
    }
    else {
      $uv='?';
    }

    $votestr .= $$VSETTINGS{voteheader} . '<small><abbr title="reputation">Rep</abbr>: '.$r.' ( '.$p.' / '.$m.' )';
    $votestr .= ' (<a href="/node/superdoc/Reputation%20Graph&id='.$nid.'">Rep Graph</a>)';
    $votestr .= ' ('.$uv.')' unless $isMine;

    $votestr .= '</small>' . $$VSETTINGS{voteFooter};
  }


  return $votestr if $isMaintenance || $inPrintMode;
  return $votestr if( $kuid && !$unkuid);

  return $votestr.'&nbsp;' if $isMine;

  unless ($$USER{votesleft}) {
    $votestr.='<strong>&nbsp; '.linkNodeTitle('Why Don\'t I Have Votes Today?|?',0).' &nbsp;</strong>' unless (exists $$VARS{nowhynovotes} and $$VARS{nowhynovotes});
    return $votestr.'&nbsp;';
  }

  $votestr.="<span class='vote_buttons'>";
  $votestr .= $$VSETTINGS{voteHeader};

  #else this is part of a checkbox group
  if ($prevvote <= 0) {
    $votestr.='<input type="radio" name="vote__'.$nid.'" '
                           #Uncomment this for revoting down
              ."value='1'" #.($prevvote>0 ?
                           #  " checked " : "")
              .($prevvote != 0 ?
                "id=\"revote__u_$nid\"" : "id=\"vote__u_$nid\"")
              ."> $uplbl";
  }

  #Don't allow revoting down
  if($prevvote == 0){
    $votestr.='<input type="radio" name="vote__'.$nid.'" '
                            #Uncomment this for revoting down
               ."value='-1'"#.($prevvote<0 ?
                            #  " checked " : "")
              .($prevvote != 0 ?
                "id=\"revote__d_$nid\"" : "id=\"vote__d_$nid\"")
              ."> $dnlbl";
  }

  if (exists $$VARS{nullvote} and $$VARS{nullvote} eq 'on' and $prevvote <= 0) {
    $votestr .= '<input type="radio" checked name="vote__'
                .getId($N)."\" value=\"$prevvote\">"
                .($prevvote == 0 ? "$nolbl" : "$dnlbl");
  }


  if (my $numvoteit = $query->param('numvoteit')) {
    $query->param('numvoteit', $numvoteit+1);
  }
  else {
    $query->param('numvoteit', 1);
  }


  $votestr .= $$VSETTINGS{voteFooter};
  $votestr .="</span>";
  $votestr = "<span id='voteinfo_$nid' style='white-space: nowrap' nowrap='nowrap'>$votestr</span>";
}                               #vote-only section


$str .= $votestr;

return $str;
