#this just gives an "under construction" notice
#first parameter is user that is making change, so we know who to blame :)
#other parameters are additional text to display

my $whoMaking = shift(@_);

my $UID = getId($USER);

my $str = '<p align="center" style="border:solid black 1px; margin:5px; padding:5px; font-family:sans-serif;"><big><big><big><strong>Under Construction</strong></big></big></big></p>
<p style="font-family:sans-serif;">This feature is currently under construction';

if((defined $whoMaking) && length($whoMaking) && (
($HTMLVARS{group_gods}{$UID}) || ($HTMLVARS{group_edev}{$UID}) || ($HTMLVARS{'group_content editors'}{$UID})
)) {
	$str .= ' by ['.$whoMaking.']';
}

$str .=', so you may experience weirdness, including, but not limited to, <font color="#dd0000">Server Error!</font>s, messed up displays, and long-winded lawyer-sounding-like sentences.';
$str .= '<br />'.join(', ',@_) if scalar(@_);
$str .= '</p>
</span>';

return $str;
