node.code:
my ($k, @title) = @_;

#inverse of this is in varcheckboxinverse
#	checked   : $$VARS is 1
#	unchecked : $$VARS doesn't exist

return if (getId($USER)==$HTMLVARS{guest_user}) || ($$USER{title} eq 'everyone') || ($$USER{title} eq 'Guest User');
my $title = join ', ', @title;
$title ||= $k;

if($query->param('setvar_'.$k)) {
	$$VARS{$k} = 1;
} elsif($query->param('sexisgood')) {
	delete $$VARS{$k};
}

$query->checkbox('setvar_'.$k, $$VARS{$k}, '1', $title);
