node.code:
my $uid = $$USER{user_id}||$HTMLVARS{guest_user};
return $query->end_form if $$NODE{type}{title} eq "e2node" && not $$NODE{group} || $uid==$HTMLVARS{guest_user} ;
my $isKiller = (exists $HTMLVARS{'group_content editors'}{$uid} || exists $HTMLVARS{group_gods}{$uid});

my $voteButton;
my $killButton;
my $rowFormat =  $$THEME{noTable} ? '<div id="votefooter">%s%s</div>' :
	'<table border="0" width="100%%"><tr><td align="left">%s</td><td align="right">%s</td></tr></table>';

if( $query->param('numvoteit') && $$USER{votesleft} ) {
	$voteButton = "<input type='submit' name='sexisgreat' id='votebutton' value='vote!'>";
} elsif ( $$USER{user_id} != $HTMLVARS{guest_user} ) {
	$voteButton = "<input type='submit' name='sexisgreat' id='blabbutton' value='blab!' title='send writeup messages'>";
}

$killButton = $isKiller && $$NODE{type}{title} ne 'draft' ? "<p><input type='submit' name='node' id='killbutton' value='The Killing Floor II'>" : '';

return sprintf($rowFormat,
	$voteButton, $killButton) . $query->end_form;
