#	}

	#format voting info
	$r = '<strong>'.$r.'</strong>' if $query->param('vote__'.$nid);
	$p = '+'.$p;
	$m = '-'.$m;
	if($uv>0) {
		$uv='+';
		$p = '<strong>'.$p.'</strong>';
	} elsif($uv<0) {
		$uv='-';
		$m = '<strong>'.$m.'</strong>';
	} else {
		$uv='?';
	}

	$str .= $$VSETTINGS{voteheader} . '<small><abbr title="reputation">Rep</abbr>: '.$r.' ( '.$p.' / '.$m.' )';

#	#controversy level (March 25, 2003 - testing)
