return $str if $kuid || $isMaintenance || $inPrintMode;

unless($$USER{votesleft}) {
	$str.='<strong>&nbsp; '.linkNodeTitle('E2 FAQ: Why Don\'t I Have Votes Today?|?',0).' &nbsp;</strong>' unless (exists $$VARS{nowhynovotes} and $$VARS{nowhynovotes});
	return $str;
}

$str .= $$VSETTINGS{voteHeader};

#else this is part of a checkbox group
$str.='<input type="radio" name="vote__'.$nid."\" value='1'> $uplbl"
  .'<input type="radio" name="vote__'.$nid."\" value='-1'> $dnlbl";

if(exists $$VARS{nullvote} and $$VARS{nullvote} eq 'on') {
	$str .= '<input type="radio" checked name="vote__'.getId($N)."\" value=\"0\"> $nolbl";
}
if(my $numvoteit = $query->param('numvoteit')) {
	$query->param('numvoteit', $numvoteit+1);
} else {
	$query->param('numvoteit', 1);
}


$str .= $$VSETTINGS{voteFooter};

} #vote-only section

$str;
