node.code:
my ( $N , $inwriteupform ) = @_ ;
getRef $N ;
$N ||= $NODE ;

my $options = '' ;
my $notification ;

unless ( $HTMLVARS{ cachedweblogoptions } ) {
	my $wls = getVars( getNode( 'webloggables' , 'setting' ) ) ;
	foreach( split ',' , $$VARS{ can_weblog } ){
		next if $$VARS{ 'hide_weblog_'.$_ };
		my $groupTitle = "News" ;
		if ( $$VARS{ nameifyweblogs } ) {
			$groupTitle = $$wls{ $_ } ;
		} else {
			$groupTitle = getNodeById($_,"light")->{title} unless $_ == 165580 ;
		}
		$options.="\n\t\t\t<option value=\"$_\">$groupTitle</option>";
	}
	$HTMLVARS{ cachedweblogoptions } = $options
		if $$NODE{type}{title} eq 'e2node' && scalar @{ $$NODE{ group } } > 1 ;
} else {
	$options = $HTMLVARS{ cachedweblogoptions } ;
	delete $HTMLVARS{ cachedweblogoptions } if $$N{ node_id } == ${ $$NODE{ group } }[-1] ;
}

my $sourceid ;
if ( $query -> param( 'op' ) eq 'weblog' and $query -> param( 'target' ) == $$N{ node_id } ) {
	$sourceid = $query -> param( 'source' ) ;
	if ( $sourceid ) {
		$options =~ s/$sourceid/$sourceid" selected="selected/ ;
		my $success = $DB -> sqlSelect( "weblog_id" , "weblog" ,
			"weblog_id=$sourceid and to_node=$$N{ node_id } and linkedby_user=$$USER{ user_id }" ) ;
		$notification = ( $success ? 'Added ' : 'Failed to add ' ) .
			"$$N{ title } to ".linkNode( $sourceid ) .
			( $success ? ' (' .
			linkNode( $NODE , 'undo' , { op => 'removeweblog' ,
			source => $sourceid , to_node => $$N{ node_id } ,
			-class=>"ajax weblogform$$N{node_id}:weblogform:$$N{node_id},$inwriteupform" } ) . ')' : '' ) ;
	} else {
		$notification = 'No page chosen: nothing added to anything.' ;
	}
}
$notification = "<p><small>$notification</small></p>" if $notification ;

return linkNodeTitle( 'Edit weblog menu[superdoc]|Edit weblog menu&hellip;' ).$notification
	if $$VARS{ can_weblog } and not $options ;

$options = "\n\t\t\t<option value=\"\" selected=\"selected\">Choose&hellip;</option>$options"
	unless $sourceid ;

my ( $class , $addnid ) = ( "wuformaction " , $$N{ node_id } ) if $inwriteupform ;
$class .= "ajax weblogform$$N{node_id}:weblogform?op=weblog&target=/target$addnid&source=/source$addnid:$$N{node_id},$inwriteupform" ;

return qq'<fieldset id="weblogform$$N{node_id}"><legend>Add this '	.
	( $$N{type}{title} eq 'e2node' ? 'entire page ' : $$N{type}{title} ) .
	qq' to a usergroup page:</legend>
		<input type="hidden" name="target$addnid" value="$$N{ node_id }">
		<select name="source$addnid">
		$options
		</select>
		<button value="weblog" name="'.( $inwriteupform ? qq'wl$addnid" type="button"' : 'op" type="submit"' ) .
		qq'class="$class">Add</button><br>
		<small>'.linkNodeTitle( 'Edit weblog menu[superdoc]|Edit this menu&hellip;' ) .
		"</small>
	$notification
	</fieldset>
	" ;
