node.page:
[%
my $theme = $query -> param( 'theme' ) ;
my $defaultStyle = getNode($$THEME{default_style},'stylesheet')->{node_id} ;
$theme = "" if $theme and getNode( $theme )->{ type }{ title } ne 'stylesheet' ;
$theme ||= $$VARS{ userstyle } || $defaultStyle;
if ( $query -> param( 'usetheme' ) and $$USER{ user_id } != $HTMLVARS{ guest_user } ) {
	$$VARS{ userstyle } = $theme  ;
	return "OK" if $query -> param( 'usetheme' ) eq 'ajax' ;
}
my $testdisplay = $query -> param( 'testdisplay' )
	unless $query -> param( 'testdisplay' ) eq 'choosetheme' ;

$query -> delete('displaytype');
$query -> param('displaytype', $testdisplay) if $testdisplay;

my $PAGE = getPage( $NODE , $testdisplay ) ;
my $str = parseCode( $$PAGE{ page } , $NODE ) ; #currently, ecore ignores the 2nd argument
if ( $$PAGE{ parent_container } ) {
	my ( $pre , $post ) = genContainer( $$PAGE{ parent_container } ) ;
	$str = $pre.$str.$post ;
}

return $str if $query -> param( 'usetheme' ) or $query -> param( 'cancel' );
my $querystring = '&amp;autofix=1' if $query -> param( 'autofix' ) ;
$str =~ s!(<link rel="s.*?zensheet.*href="\D+)\d+([^"]*)(".*?>)!$1$theme$2$querystring$3! if
	$theme.$querystring ne $$VARS{ userstyle } || $defaultStyle;

# Get list of stylesheets sorted by popularity
# ============ nearly same code as Theme Nirvana =============
# only show themes for "active" users (in this case lastseen within 6 months
my ($sec,$min,$hour,$mday,$mon,$year) = gmtime(time - 15778800); # 365.25*24*3600/2
my $cutoffDate = ($year+1900).'-'.($mon+1)."-$mday";
#my $defaultStyle = getNode($$THEME{default_style},'stylesheet')->{node_id} ;

my $rows = $DB->sqlSelectMany( 'setting.setting_id,setting.vars' ,
	'setting,user' ,
	"setting.setting_id=user.user_id 
		AND user.lasttime>='$cutoffDate' 
		AND setting.vars LIKE '%userstyle=%'
		AND setting.vars NOT LIKE '%userstyle=$defaultStyle%'" ) ;

my $dbrow ;
my %styles ;
while($dbrow = $rows->fetchrow_arrayref)
{
   $$dbrow[1] =~ m/userstyle=([0-9]+)/;
   if (exists($styles{$1}))
   {
      $styles{$1} = $styles{$1}+1;
   }
   else
   {
      $styles{$1} = 1;
   }
}
sub hashValueDescendingNum
{
   $styles{$b} <=> $styles{$a};
}
my @keys = sort hashValueDescendingNum (keys(%styles)) ;
unshift( @keys , $defaultStyle ) ;
# ======== end nearly same code ========
unshift( @keys , $theme ) unless $styles{ $theme } or $theme eq $defaultStyle ;
my $widget = '' ;
if($$USER{ user_id } != $HTMLVARS{ guest_user } ){
	foreach ( @keys ) {
		my $n = getNodeById( $_ );
		next unless $n ;
		$widget .= "
			<option value=\"$_\"" ;
		$widget .= ' selected="selected"' if $_ eq $theme ;
		$widget .= '>'.$$n{ title } ;
		$widget .= '*' if $_ eq $$VARS{ userstyle } ;
		$widget .= '</option>' ;
	}

	$widget = htmlcode( 'openform' , 'widget' ).
	'<h3 id="widgetheading">Test theme: <em>'.getNodeById( $theme ) -> { title }.'</em></h3>
	<div>
	<label>Choose a theme:
		<select name="theme">'.$widget.'
		</select>
	</label>
	<noscript>
		<input type="hidden" name="noscript" value="noscript">
		<input type="submit" name="test" value="Test this theme">
	</noscript>
	<input type="submit" name="usetheme" value="Use this theme">
	<input type="submit" name="cancel" value="Cancel">
	<br>
	<small>Click on links to test this theme on other pages.
	Bugs to <a href="/user/DonJaime">DonJaime</a>.</small>
	</div>
	</form>
	' ;
}
my $widgetstyle = '
<style type="text/css">
html body form#widget {
	position: absolute ;
	position: fixed ;
	top: 5em ; left:5em ;
	z-index: 1000000 ;
	font: 16px sans-serif normal ;
	background: #ffd ;
	color:black;
	border: 1px solid black ;
	padding: 0.25em ;
}
html body form#widget > * {
	font: inherit ;
	color: inherit ;
	background: inherit ;
}
html body form#widget h3 {
	font-weight: bold ;
	margin: 0 0 0.5em ;
	padding: 0.125em 0.25em ;
	color: #ffd ;
	background: black ;
}
html body form#widget small {
	border-top: 1px solid black ;
	display: block ;
	margin-top: 0.5em ;
	font-size: 75% ;
}
'. # old IE hack:
'* html #widget { position: absolute ; }
</style>
' ;

$str =~ s!(<body.*?>)!$1$widget! ;
$str =~ s!</head>!$widgetstyle</head>! ;
unless ( $query -> param( 'noscript' ) ) {
	# if they have scripting, let their computer do the work
	$querystring = '&autofix=1' if $querystring ;
	my $script = qq'
<script type="text/javascript">
	var zenSheet = jQuery( "#zensheet" ) ;
	var titletext = jQuery( "#widgetheading em" )[0] ;
	var widget = jQuery("#widget")[0];
	var theme = "$theme" ;

	jQuery( widget.theme ).bind("change", function() {
		theme = this.value ;
		zenSheet.attr( "href" , "/index.pl?node_id=" + theme +
			"&displaytype=serve$querystring" ) ;
		titletext.nodeValue = this[ this.selectedIndex ].firstChild.nodeValue ;
	});
	widget.usetheme.onclick = function() {
		jQuery.ajax( {url:this.form.action,
			data: { displaytype: "choosetheme" , usetheme: "ajax" , theme: theme } ,
			success: cleanup } ) ;
		return false ;
	}
	widget.cancel.onclick = function() {
		zenSheet.attr( "href" , "'.
			htmlcode('linkstylesheet',$$VARS{ userstyle }||$$THEME{default_style},'serve').$querystring.qq'" ) ;
		cleanup() ;
		return false ;
	}
	function cleanup() {
		document.body.removeChild( widget ) ;
		jQuery("a").unbind("focus",changehref).unbind("blur",unchangehref);
 	}
 	function changehref(){
		if (this.savehref) return;
		this.savehref = this.href ;
		// cripple external link:
		if ( this.getAttribute( "href" ).match( /^\w+:.*\$/ ) )
			this.href = "/" ;
		// rename any displaytype parameter:
		this.href = this.href.replace( /(\\?|&)displaytype=/ , "\$1testdisplay=" ) ;
		// add parameters to existing queries:
		this.href = this.href.replace( /\\?(.*)/ ,
			"?displaytype=choosetheme$querystring&theme=" + theme + "&\$1" ) ;
		// add query if was none
		if ( this.href.match( /\\?/ ) ) return ;
		this.href = this.href.replace( /\$|(#.*)/ , "?displaytype=choosetheme$querystring&theme=" + theme + "\$1" ) ;
	}
	function unchangehref(){
		this.href = this.savehref ;
		this.savehref = null;
	}
	jQuery("a").bind("focus click", changehref ).bind("blur", unchangehref) ;
	jQuery(widget).draggable().css("cursor","move");
</script>
' ;
	$str =~ s!</body>!$script</body>! ;
	return $str  ;
}
# fix links server-side if no scripting available
$querystring = "?displaytype=choosetheme&amp;theme=$theme$querystring&amp;noscript=1" ;
# cripple external links:
$str =~ s!(<a [^>]*?href\s*=\s*['"])\w+:[^'">]*!$1/!g ;
# rename any displaytype parameters:
$str =~ s!(<a [^>]*?href\s*=\s*['"][^?'">]*?\?[^'">]*?)displaytype=!$1testdisplay=!g ;
# add parameters to existing queries:
$str =~ s!(<a [^>]*?href\s*=\s*['"][^?'">]*?)\?!$1$querystring&amp;!g ;
# add query where is none:
$str =~ s!(<a [^>]*?href\s*=\s*['"][^&'">]*?)((?:#[^'">]*?)?['"].*?>)!$1$querystring$2!g ;
$str ;
%]
