node.page:
[%

my @junk=(
    "Where did I put that?"
    , "I lost my mind in San Francisco"
    , "Shuffle shuffle"
    , "I smashed it into a million pieces, and cut myself on its beauty"
    , "It used to be full of stars, but now I'm full of scars."
  );

return qq-<small><p align="right"><strong>$junk[rand(@junk)]</strong></p></small>-;

%]

[%
  my $softlinkType = 0;
  my $E2NODE = $NODE;
  my $csr =
    $DB->sqlSelectMany(
      "to_node"
      , "links"
      , "from_node = $$E2NODE{node_id} AND linktype = $softlinkType"
    );

  my @LINKS;

  while(my $row = $csr->fetchrow_hashref)
  {
    my $N = getNodeById($$row{to_node}, 'light');
    next unless $N;
    push @LINKS, $N;
  }
  return "<p>Ain't nothin'</p>" unless @LINKS > 0;
  my $str = ""
    . "<p>"
    . "Somewhere near " . linkNode($E2NODE) . " I got lost in:"
    . "</p>"
    ;
  $str .= '<div id="softlinks">';
  foreach(sort {rand() <=> rand()} @LINKS)
  {
    my $fontSize = int((rand(8)**2.2+65)) . '%';
    $str .= 
     linkNode(
       $_
       , undef
       , {
           lastnode_id => $$E2NODE{node_id}
           , -style=> "font-size: $fontSize;"
         }
     );

    $str .= "&nbsp;" x (rand(50));
  }
  $str .= '</div>';

  return $str;
%]
