node.page:
[%
return "" unless isGod($USER);
if ($$USER{title} eq 'ascorbic') {
  if ($query->param('addnodelet')) {
    my (@nls)=split(/,/,$$VARS{nodelets});
    push @nls, $$NODE{node_id};
    my %seen = ();
    my $item;
    my @nl;
    foreach $item (@nls) {
      push(@nl, $item) unless $seen{$item}++;
    }

    $$VARS{nodelets} = join(',',@nl);
  } else {
    return "<a href='" . urlGen({node_id=>getId($NODE), addnodelet => 1}) . "'>Add this nodelet</a>";
  }
}
%]
[{listcode:nlcode}]
