my ($writername, $readername);

if ($wu) { $writername = getNodeById($$wu{'author_user'}) -> {'title'}; }
if ($$NODE{'read_by'}) { $readername = getNodeById($$NODE{'read_by'}) -> {'title'};  }

$str .="<table>";

$field="title";
