node.page:
[%
use POSIX qw(ceil floor);

return unless isGod($USER);

my $open = "1\;";
my $locked = "0\;";

my $otherone;
my $title = "";
if ($$NODE{criteria} eq $open) {
  $title = "lock";
  $otherone = $locked;
} elsif ($$NODE{criteria} eq $locked) {
  $title = "unlock";
  $otherone = $open;
} else {
  return;
}

"<font size=1><i>".linkNode($NODE, $title, {room_criteria=>$otherone})."</i></font>";


%]
<p>

[%

  return "You cannot go into $$NODE{title}, I'm sorry." unless eval($$NODE{criteria}) and not getId($USER) == $HTMLVARS{guest_user};
if ($$VARS{lockedin} > time)
	{
	my $remainingtime = ceil(($$VARS{lockedin} - time)/ 60);
	my $lockmessage = "<p><strong style='color:red;'>"
		. "You cannot change rooms for $remainingtime minutes.  "
		. "You can still send private messages, however, or talk to people in your current room.  "
		. "</strong></p>";
	return $lockmessage;
	}

  use Everything::Room;
  changeRoom($USER, $NODE);

  # For room usage counting:
  my (undef, undef, undef, $day, $mon, $year) = localtime();
  $NODE->{lastused_date} = join "-", ($year+1900,++$mon,$day);
  updateNode($NODE, -1);

  return "You walk into $$NODE{title}";

%]
<p>
[{parselinks:doctext}]
[%
  return "<br><p align=\"right\">(".linkNode(getNode("go outside", "superdocnolinks")).")</p>";
%]
