node.page:
[%
return unless(isGod($USER));

my $str.='<i>'.parseLinks($$NODE{doctext}).'</i><br /><br />This query is described in SQL by:<br /><blockquote><code>'.encodeHTML($$NODE{sqltext})."</code></blockquote><br />\n";

$str.='<br />There are '.$DB->sqlSelect('count(*)', 'sqlresult', 'sqljob_id='.$$NODE{node_id}).parseLinks(' records found by the job last Word Galaxy...<br><br><p align="center">Back to [SQL Center]</p>');

$str.='<ul>';

my $csr = $dbh->prepare('SELECT * FROM sqlresult where sqljob_id='.$$NODE{node_id}.' LIMIT 500');

$csr->execute();

my $nr = getId(getNode('node row', 'superdoc'));
while(my $row = $csr->fetchrow_hashref()) {

	my $resnode = getNodeById($$row{result_id});
	next unless($resnode);
	$str.='<li>'.linkNode($resnode, $$resnode{title});


     if($$resnode{type_nodetype} == getId(getType('writeup')))
     {
	my $killer = $DB->sqlSelect('linkedby_user', 'weblog', 'weblog_id='.$nr.' and to_node='.$$resnode{node_id}) || 0;

	my $kstring = '';

	$kstring = ' <small>(marked for destruction)</small>' if($killer);

	$str .= '&nbsp;&nbsp;&nbsp;&nbsp;('.linkNode(getNodeById($$resnode{parent_e2node}), 'full').')&nbsp;&nbsp;&nbsp;<small>Rep:'.$$resnode{reputation}.'</small>'.$kstring.'<br />'.encodeHTML($$resnode{doctext}).'<br />';
     }
     elsif($$resnode{type_nodetype} == getId(getType('e2node')))
     {

        my $nLinks = $DB->sqlSelect('count(*)', 'links', 'from_node='.$$resnode{node_id}.' AND linktype=0');

        my $nWUs = $DB->sqlSelect('count(*)', 'writeup', 'parent_e2node='.$$resnode{node_id});

        my $e2nLinks = '';
        my $e2nWUs = '';

        if($nLinks == 0)
        {
           $e2nLinks = 'No softlinks';
        }
        else
        {
           $e2nLinks = 'Softlinks: '.$nLinks;
        }

        if($nWUs == 0)
        {
            $e2nWUs = 'Nodeshell';
        }
        else
        {
            $e2nWUs = 'Writeups: '.$nWUs;
        }

        $str.='&nbsp;&nbsp;&nbsp;&nbsp;('.$e2nWUs.' , '.$e2nLinks.')';

     }

}

$str.='</ul>';

return $str;
%]
