node.page:
[%
if($$VARS{preferred_theme} && $$VARS{preferred_theme}!=1854183) {
	return "/* You need to switch to the Zen theme in [User Settings] to use Zen stylesheets. */";
} 
if ($query->param('version')) {
  # Set a far-future expiry time if a specific version is requested.
  $HEADER_PARAMS{'-expires'} = '+10y';
}

my $out = $$NODE{doctext};
my $autofix = $query -> param( 'autofix' );
if ( $autofix or $$USER{ user_id } != $HTMLVARS{ guest_user } ) {
	unless ( htmlcode( 'displaySetting' , 'fixed stylesheets', $$NODE{ node_id } ) >= $HTMLVARS{stylesheetFixLevel} ) {
		$out = "/*autofixed*/\n".htmlcode( 'fixstylesheet' , 0 ) if
			$$USER{ user_id } != $$NODE{ author_user } or
			$autofix ;
	}
}

$out =~ s/^\s+//mg;
$out .= "/* SOFTLINK COLORS */\n";

my $styleRule = '\\b\\s*(?:,[^{]*)?{[^}]*background[^;}]*#(?:(\\w\\w)(\\w\\w)(\\w\\w)|(\\w)(\\w)(\\w))';
my @max = ( 255, 255, 255 ) ;
my @min = ( 170, 170, 170 ) ;
@max = (hex($1||$4.$4),hex($2||$5.$5),hex($3||$6.$6)) if
	$out =~ /#sl1$styleRule/ ||
	$out =~ /#mainbody$styleRule/ ||
	$out =~ /\bbody$styleRule/ ;
@min = (hex($1||$4.$4),hex($2||$5.$5),hex($3||$6.$6)) if
	$out =~ /#sl64$styleRule/ ||
	$out =~ /\.slend$styleRule/ ||
	$out =~ /\.oddrow$styleRule/ ;


for (my $i=64; $i; $i--){
	$out .= "td#sl$i\{background:#".(
		join '' , ( map sprintf( '%02x', int($max[$_]-($i-1)*($max[$_]-$min[$_])/63) ), (0..2) )
		).';}';
	$out .= "\n" if $i % 4 == 1 ;
}

return $out;
%]
