node.page:
[{setupuservars}]
<div id='homenodeheader'>
[{homenodeinfectedinfo}]
<div id='homenodepicbox'>
[%
return unless $DB->isApproved($NODE, getNode('users with image', 'nodegroup')) or getLevel($NODE) >= 1;
return unless $$NODE{imgsrc};
'<img src="http://images.everything2.com/'.$$NODE{imgsrc}.'" id="userimage">'; 
%]
[%
return if getId($USER) != getId($NODE);
'<p>' . linkNode($NODE, '(edit user information)', {displaytype=>'edit'}) . '</p>';
%]
</div>
[{zenDisplayUserInfo}]
</div>

<hr class='clear'>

<table width="100%" id='homenodetext'><tr><td>
[%  

  return if (htmlcode("isGuest"));
  my $csr = $DB->sqlSelectMany('*','registration',
    'from_user='.$$NODE{user_id}.' && in_user_profile=1')
      ||return 'SQL Problem.  Please notify a [coder].';
  my $labels = ['Registry','Data','Comments'];
  my $rows;
  while(my $ref = $csr->fetchrow_hashref()){
    push @$rows,{
      'Registry'=>linkNode($$ref{for_registry}),
      'Data'=>breakTags(parseLinks(htmlScreen($$ref{data}))),
      'Comments'=>breakTags(parseLinks(htmlScreen($$ref{comments}))),
    };
  }

  return buildTable($labels,$rows,'class="registries",nolabels') if($rows);
  return '';
%]
<div class='content'>
[{displayUserText}]
</div>
</td></tr></table>





