node.nlcode:
[{googleSearchForm}]
[%



my ($loginStr, $votesLeftStr, $expStr);

#####LOGIN STRING

$loginStr .= linkNode($USER,'My Homenode',{displaytype=>'edit',lastnode_id=>0})."<br />";
$loginStr.= linkNode($HTMLVARS{userSettings_node}, 'My Preferences',{lastnode_id=>0}) ."<br />";
$loginStr.= linkNode(getNode('Multiple Scratch Pad Viewer','superdocnolinks'),'My Scratch Pad') ."<br />";

###VOTES LEFT, XP

my @thingys = ();

my $c = $$VARS{cools} || 0;
my $v = $$USER{votesleft} || 0;

if($v !~ /^\d+$/) { $v = 0; }

if ($c || $v) {
	if($c) { push @thingys, '<strong id="chingsleft">'.$c.'</strong> C!'.($c>1?'s':''); }
	if($v) { push @thingys, '<strong id="votesleft">'.$v.'</strong> vote'.($v>1?'s':''); }

	if (scalar(@thingys)) {
		$votesLeftStr = '<p id="votesleft">You have ' . join(' and ',@thingys) . ' left today.</p>';
	}
}

$expStr = '<p id="experience">'.htmlcode('shownewexp','TRUE').'</p>';

my $epicenterStr = $loginStr . $votesLeftStr . $expStr;
return $epicenterStr;

%]

