node.nodetest_code:
my ($E2NODE) = @_;
return "y" unless exists($$E2NODE{group});
return "y" if $$E2NODE{group} == 0;

my $links = $DB->sqlSelect("count(*)", "links","from_node=$$E2NODE{node_id}");
return "y" if $links > 0; 
return "n";

node.nlcode:
my ($E2NODE) = @_;
return "y" unless exists($$E2NODE{group});
return "y" if $$E2NODE{group} == 0;

my $links = $DB->sqlSelect("count(*)", "links","from_node=$$E2NODE{node_id}");
return "y" if $links > 0; 
return "n";
