node.nodetest_code:
my ($WRITEUP) = @_;
my $webby = getNode("Webster 1913", "user");
return "y" if($$WRITEUP{author_user} == $$webby{node_id});

my $count = $DB->sqlSelect("count(*)", "vote", "vote_id=$$WRITEUP{node_id}");
return "n" if $count == 0;
return "y";

node.nlcode:
my ($WRITEUP) = @_;
my $webby = getNode("Webster 1913", "user");
return "y" if($$WRITEUP{author_user} == $$webby{node_id});

my $count = $DB->sqlSelect("count(*)", "vote", "vote_id=$$WRITEUP{node_id}");
return "n" if $count == 0;
return "y";
