node.nodetest_code:
my ($WRITEUP) = @_;

my $types;
my @correct = ("person","place","thing","idea","definition");
foreach(@correct)
{
  my $t = getNode($_, "writeuptype");
  next unless $t;
  $$types{$$t{node_id}} = 1;
}
 
return "y" if exists($$types{$$WRITEUP{wrtype_writeuptype}});
return "n";

node.nlcode:
my ($WRITEUP) = @_;

my $types;
my @correct = ("person","place","thing","idea","definition");
foreach(@correct)
{
  my $t = getNode($_, "writeuptype");
  next unless $t;
  $$types{$$t{node_id}} = 1;
}

return "y" if exists($$types{$$WRITEUP{wrtype_writeuptype}});
return "n";
