local *hasMsgAccess = sub {
  my ($msg, $user) = @_;
  return unless($msg);
  return if($msg->{author_user} != $user->{node_id} and not isGod($user));
  return 1;
};

local *deleteMsg = sub {
    my ($MSG, $user) = @_;
    return unless hasMsgAccess($MSG, $user);
    $DB->sqlDelete("message", "message_id=$$MSG{message_id}");
    return 1;
};

local *archiveMsg = sub { 
    my ($MSG, $user, $archivebit) = @_;
    return unless hasMsgAccess($MSG, $user);
    $archivebit = 1 unless defined($archivebit);
    $DB->sqlUpdate("message", {archive=>$archivebit}, "message_id=$$MSG{message_id}");
    return 1;
};

local *spaceSplit = sub {
   my ($text) = @_;
   if($text =~ /(.*?)\s*(.*)/)
   {
     return ($1, $2);
   }else
   {
     return ($text, "");
   }
};

local *getUserSmart = sub {
    my ($name) = @_;
    return unless $name;
    return $name if(ref($name) eq "HASH");
    my $n = getNode($name, "user");
    $n ||= getNode($name, "usergroup");
    return $n;
};

local *getMsgQueue = sub {
   my ($userorgroup, $from) = @_;
   return unless $userorgroup;

   if($userorgroup->{type_nodetype} == getType("user")->{node_id}){
      return [$userorgroup->{node_id}]
   }elsif($userorgroup->{type_nodetype} == getType("usergroup")->{node_id}){
      unless(Everything::isApproved($from, $userorgroup)){
          return ($DB->selectNodegroupFlat($usergroup), 1);
      }
   }else{
      return;
   }
    return;
}

local *sendMsg = sub {
    my ($to, $from, $msgtext) = @_;
    $to = getUserSmart($to);
    $from = getUserSmart($from);
    return unless defined($to) and defined($from) and $msgtext;

    my (@$msgq, $isGrp) = getMsgQueue($to);

    foreach(@$msgq){
      my $msghash = {msgtext => $msgtext, author_user => $from->{node_id}, for_user => $_};
      $msghash->{for_usergroup} = $to->{node_id} if($isGrp);
      $DB->sqlInsert('message', $msghash);
    }
};

local *inviteUser = sub  {
             my ($invitee) = @_;
             my $room;
             if($room = getNodeById($USER->{in_room})){
                $room = $room->{title};
             }else{
                $room = "go outside|outside";
             }

             sendMsg($invitee, $USER, "come join me in [$room]");
         };

local *getMessageRecips = sub {
    my($msgbody) = @_;
    my ($to, $body);

    if($msgbody =~ s/^\{(.*?)\}\s*//g)
    {
        @$to = split(/\s*/,$1);
        $body = $msgbody;
    }else{
        ($to, $body) = spaceSplit($msgbody);
        @$to = [$to];
    }

    return ($to, $body);

};

local *sendPrivateMessage = sub {
   my($msgbody, $ONO) = @_;
   $ONO ||= 0;

   my ($to, $body) = getMessageRecips($msgbody);

   foreach(@$to)
   {
     sendMsg($_, $USER, $body);
   }

};

my $msgops = {
     "delete" = sub { deleteMsg(shift, shift) },
     "archive" = sub { archiveMsg(shift, shift) },
     "unarchive" = sub { archiveMsg(shift, shift, 0) },
};

my $msgtypes = {
     "msg"        => sub { sendPrivateMessage(shift) },
     "msg?"       => sub { sendPrivateMessage(shift, 1) },
     "tell"       => sub { sendPrivateMessage(shift) },
     "tell?"      => sub { sendPrivateMessage(shift, 1) },
     "borg"       => 1,
     "topic"      => 1,
     "sayas"      => 1,
     "chatteroff" => 1,
     "chatteron"  => 1,
     "invite"     => \&inviteUser,

     "say"        => 1,

};

