node.doctext:
[{crtnavbar}]
[%
    my $activecrt = getNode('activecrt', 'usergroup');
    my $str = "Add to usergroup ([activecrt] only):<br>".htmlcode('openform')."<input type=\"hidden\" name=\"crtview\", value=\"addtousergroup\"><input type=\"text\" name=\"crtuser\"><input type=\"submit\" value=\"Add to activecrt\"><br><br>";

    if($query->param('crtuser'))
      {
       my $usr= getNode($query->param('crtuser'), 'user');
       if ($usr){
          $activecrt = insertIntoNodegroup($activecrt, -1, $usr);
          updateNode($activecrt, -1);
          $str.= "user ".linkNode($usr)." added to ".linkNode($activecrt);
         }
         else
         {
              $str.='<font color="red">No such user</font>';
         }
      }

    $str.="<br><br>";

    my $grp = $$activecrt{group};
    foreach(@$grp)
    {
       $str.=linkNode(getNodeById($_))." ";
    }

    return $str;
%]
