node.doctext:
[%
sub mysort {
 lc($a) cmp lc($b);
}

my $queryText;
my $rows;
my $dbrow;
my $str;
my @list;
my @sortedList;
my $key;
my $n;

my $nodeId = getId($NODE);
my $uid = getId($USER)||0;
my $dbh = $DB->getDatabaseHandle();
return 'No database handle!' unless $dbh;

$queryText = "SELECT user.user_id,user.experience FROM setting,user WHERE setting.setting_id=user.user_id AND setting.vars LIKE '%hasRecalculated=1%'";

$rows = $dbh->prepare($queryText)
   or return $rows->errstr;
$rows->execute()
   or return $rows->errstr;

while($dbrow = $rows->fetchrow_arrayref)
{
   push(@list, linkNode($$dbrow[0]) . ' - Level: ' . getLevel($$dbrow[0]) . ' - XP: ' . $$dbrow[1]);
}

$str .= '<h3>Users who have run [Recalculate XP]</h3>';
$str .= '<ol style="margin-left:55px">';
foreach $key (sort mysort @list)
{
  $str .= '<li>'.$key.'</li>';
}
$str .= '</ol>';

return $str;
%]
