node.doctext:
[{changeroom}]
[%

use Time::HiRes;
my @start = Time::HiRes::gettimeofday;
my $timeStr;
my $timeCount = 1;

my $str;
my $wherestr;

if($USER->{in_room})
{
  $wherestr.=' room_id='.int($USER->{in_room});
} elsif (!getNodeById($USER->{in_room})) {
  $USER->{in_room} = 0;
}

my $UID = $$USER{node_id};
my $isRoot = exists $HTMLVARS{'group_gods'}{$UID};
my $isCE = $isRoot || exists $HTMLVARS{'group_content editors'}{$UID};
my $isEdev = exists $HTMLVARS{'group_edev'}{$UID};
my $isNinja = htmlcode('isinUsergroup',$UID.',1336287');

unless($isRoot || $$VARS{infravision}) {
	$wherestr.=' AND ' if $wherestr;
	$wherestr.='visible=0';
}

my $showActions = $$VARS{showuseractions} ? 1 : 0;

my @doVerbs;
my @doNouns;
if($showActions) {
@doVerbs = (
	'eating', 'watching', 'stalking', 'filing',
	'noding', 'amazed by', 'tired of', 'crying for',
	'thinking of', 'fighting', 'bouncing towards',
	'fleeing from', 'diving into', 'wishing for',
	'skating towards', 'playing with',
	'upvoting', 'learning of', 'teaching',
	'getting friendly with', 'frowned upon by',
	'sleeping on', 'getting hungry for', 'touching',
	'beating up',
	''	#leave this blank one in, so the verb is sometimes omitted
);
@doNouns = (
	'a carrot', 'some money', 'EDB', 'nails', 'some feet',
	'a balloon', 'wheels', 'soy', 'a monkey', 'a smurf',
	'an onion', 'smoke', 'the birds', 'you!', 'a flashlight',
	'hash', 'your speaker', 'an idiot', 'an expert', 'an AI',
	'the human genome', 'upvotes', 'downvotes',
	'their pants', 'smelly cheese', 'a pink elephant',
	'teeth', 'a hippopotamus', 'noders', 'a scarf',
	'your ear', 'killer bees', 'an angst sandwich'
);
}

my $showRoots = 1;
my $showContentEds = 1;
my $showDevs = $isEdev;

my $newbielook = $isRoot || $isCE;

my $powStructLink = '<a href='.urlGen({'node'=>'The Power Structure of Everything2', 'type'=>'superdoc'}) . ' style="text-decoration: none;" ';
my $linkRoots = $powStructLink . 'title="e2gods">+</a>';
# April fools -- or is it?
my $linkNinjas = $powStructLink . 'title="ninjagirls">@</a>';
#old-fashioned editors were indicated with a +
my $linkCEs = $powStructLink . 'title="Content Editors">$</a>';
my $linkDevs = $showDevs ? ( '<a href='.urlGen({'node'=>'edev', 'type'=>'usergroup'}) . ' style="text-decoration: none;" title="edev">%</a>' ) : 0;
my $linkBorged = '<a href='.urlGen({'node'=>'E2 FAQ: Chatterbox', 'type'=>'superdoc'}) .' style="text-decoration: none;" title="borged!">&#216;</a>';

my $csr = $DB->sqlSelectMany('*', 'room', $wherestr, 'order by experience DESC');
#my $csr = $DB->sqlSelectMany('*', 'room', $wherestr, 'order by rand()');
#my $csr = $DB->sqlSelectMany('*', 'room', $wherestr, 'order by (numwriteups * merit) DESC');


#N-Wing steals JB's room topic thing, and uses it for users
my $userInfo = getVars(getNodeById(1177941)) || 0; #hard-coded ID for speed to ('User Info','setting')

my $num = 0;
my $sameUser;	#if the user to show is the user that is loading the page
my $userID;	#only get member_user from hash once
my $n;	#nick
my $is1337 = ($userID == 220 || $userID == 322);	#nate and hemos

my $ignorelist = $DB->sqlSelectMany('ignore_node', 'messageignore', 'messageignore_id='.$UID);
  
my @list;
while(my ($u) = $ignorelist->fetchrow())
{
  push @list, $u;
}

local *in_an_array = sub {
  my $needle = shift;
  my @haystack = @_;

  for (@haystack) {
    return 1 if $_ eq $needle;
  }
  return 0;
};

while(my $U = $csr->fetchrow_hashref())
{
  $num++;
  $userID = $$U{member_user};
  $sameUser = $UID==$userID; #integer test fast
  next if in_an_array($userID, @list) && (!$isRoot);
  my $nameLink = linkNode($userID, $n=$$U{nick}, {lastnode_id=>0});
  $nameLink = '<strong>'.$nameLink.'</strong>' if $sameUser || $is1337;

  my $flags='';
  if($showRoots && exists $HTMLVARS{'group_gods'}{$userID}) {
    $flags .= $linkRoots;
  }

  if($newbielook && !($is1337)) {
    my $getTime = $DB->sqlSelect("datediff(now(),createtime)+1 as difftime","node","node_id=".$userID." having difftime<31");

    if ($getTime) {
      if ($getTime<=3) {
        $flags.='<strong>'.$getTime.'</strong>';
      }
      else {
        $flags.=$getTime;
      }
    }
  }

  if($showContentEds && exists $HTMLVARS{'group_content editors'}{$userID}) {
    $flags .= $linkCEs;
  }

  if($isNinja) {
    $flags .= $linkNinjas;
  }

  if($showDevs && exists $HTMLVARS{'group_edev'}{$userID}) {
    $flags .= $linkDevs;
  }
  $flags .= $linkBorged if $$U{borgd}; #yes, no 'e' in 'borgd'
  if($$U{visible}) {
    $flags.='<font color="#ff0000">i</font>';
  }

  if($$U{room_id} != 0 and $$USER{in_room} == 0)
  {
    my $rm = getNodeById($$U{room_id});
    $flags.='<a href='.urlGen({'node_id' => $$U{room_id}}).' title="in room: '.$$rm{title}.'" style="text-decoration: none;">~</a>';
  }

  $flags = ' &nbsp;[ <big>' . $flags . '</big> ]' if $flags;

  if($showActions && !$sameUser && (0.02 > rand())) {
    $nameLink.=' <small>is ' . $doVerbs[int(rand(@doVerbs))] . ' ' . $doNouns[int(rand(@doNouns))] . '</small>';
  }

  $n =~ tr/ /_/;
  if($userInfo && exists $$userInfo{$n}) {
    $nameLink .= '<small>'.$$userInfo{$n}.'</small>';
  }

  $str .= $nameLink . $flags . '<br />';
}
$csr->finish;

# Apatrix 20080817 - commented out because both exceptions are no longer admins.
#if ($$USER{node_id} == 1312198 or $$USER{node_id} == 1122144) {
#  $timeStr = 'Elapsed - '.$timeCount.': ' . Time::HiRes::tv_interval(\@start, [ Time::HiRes::gettimeofday ]) . ' s<br />';
#  $timeCount++;
#  $str .= $timeStr;
#}

#jay's topic idea.  Our slow cruel descent into IRC
my $topicsetting;
my $topic = '';

unless(($$VARS{topicCbox})||($$VARS{hideTopic})) {
  $topicsetting = getVars(getNodeById(1149799)); #hard coded id for speed ('Room Topics','setting')

  if(exists($$topicsetting{$$USER{in_room}})) {
    $topic = $$topicsetting{$$USER{in_room}};

    $topic = '<hr width="99%" />'.$topic if $$VARS{topicSeparator};
    $topic = '<small>'.parseLinks($topic).'</small><br /><hr width="100" />';
  }
}

return '<em>There are no noders in this room.</em>' unless $num;

my $intro = 'Your fellow noders ('.$num.'): ';
$intro.='<small>in '.linkNode($$USER{in_room}).'</small><br />' if $$USER{in_room};
$intro.='<br>';

return $topic . $intro . $str;
%]

