node.doctext:
[{openform}]

[%
my $result;

my $input = $query->param('skratch');

if ($input) {
	$result .= parseLinks($input . '', $NODE);
} else {
	$result .= '<i>blank</i>';
}

$result .= "<p>";

$result .= $query->textarea('skratch', $input, 20, 60, "", "wrap=virtual");

return $result;
%]

[{closeform}]
