node.doctext:
[%
return '<p>Old code never dies. But this is retired.</p>';

my $str = '';

#new restrict check for any usergroup
my $UID = getId($USER);
my $isRoot = exists $HTMLVARS{group_gods}{$UID};

return if !$isRoot;

return "No poll ID!" if (!defined($query->param("e2poll_id")));

my $thePoll = $query->param("e2poll_id");

getRef($thePoll);

my $optstr=$$thePoll{e2poll_results};
my $normalVotes = substr($optstr,0,rindex($optstr,","));
my $notaVote = substr($optstr,rindex($optstr,",")); #None of the Above

my $count2=0;

my @personal = split(/,/, $optstr);
my $optsize = @personal;
for ($count2=$optsize;$count2<7;$count2++) {
$normalVotes.=",0";
}
$optstr = $normalVotes.$notaVote;

my $doctext = $$thePoll{doctext};
$doctext =~ s/\[\%//g;
$doctext =~ s/\%\]//g;
my $docresult = eval($doctext);
	my $options = $HTMLVARS{options};

my $question = substr($docresult,0,index($docresult,"</p>"));

$question =~ s/\[/&#091;/g;
$question =~ s/\]/&#093;/g;
$question =~ s/\<p\>//g;

$str .= '

<form name="pollmaker" method="post">
<input type="hidden" name="op" value="polledit">
<input type="hidden" name="e2poll_id" value="'.$query->param("e2poll_id").'">
<table>
<tr>
<td>Poll Title</td>
<td>'.$$thePoll{title}.' (to edit the title, '.linkNode($thePoll,"click here",{displaytype => "edit"}).')</td>
</tr>
<tr>
<td>Poll Question</td>
<td><input type="text" size="50" maxlength="128" name="poll_question" value="'.$question.'"></td>
</tr>';

my $kc = 0;
foreach my $key (sort {$a <=> $b} (keys(%$options))) {
my $value = $$options{$key};
$value =~ s/\[/&#091;/g;
$value =~ s/\]/&#093;/g;
$str.='
<tr>
<td>Poll Option</td>
<td><input type="text" size="50" maxlength="64" name="option'.($key+1).'" value="'.$value.'"></td>
</tr>' if $value ne 'None of the above.';
$kc = $key;
}

my $count = $kc;
$count++;

$str.='
<tr>
<td><b>New</b> Poll Option</td>
<td><input type="text" size="50" maxlength="64" name="option'.$count.'" value=""></td>
</tr>';

$count++;

$str.='
<tr>
<td><b>New</b> Poll Option</td>
<td><input type="text" size="50" maxlength="64" name="option'.$count.'" value=""></td>
</tr>';

$count++;

$str.='
<tr>
<td><b>New</b> Poll Option</td>
<td><input type="text" size="50" maxlength="64" name="option'.$count.'" value=""></td>
</tr>';


$str.='</table>';
$str.='<input name="sexisgood" type="submit" value="Update Poll">
</form>
';


$str;
%]
