node.doctext:
[%
my $dbh = $DB->getDatabaseHandle();
return 'No database handle!' unless $dbh;
my $rows;
my $str;
my $dbrow;
my $queryText;
my $limit = 100;
my $recalcRows;
my $row;

$queryText = 'SELECT SUM(GP) FROM user';
$rows = $dbh->prepare($queryText)
   or return $rows->errstr;
$rows->execute()
   or return $rows->errstr;
my $totalGP = $rows->fetchrow_array();

# get recalculated noders
$queryText = "SELECT user.user_id FROM setting,user WHERE setting.setting_id=user.user_id AND setting.vars LIKE '%hasRecalculated=1%'";
$rows = $dbh->prepare($queryText)
   or return $rows->errstr;
$rows->execute()
   or return $rows->errstr;
my @recalcUsers;
while($row= $rows->fetchrow_arrayref)
{
  push(@recalcUsers, $$row[0]);
}


$queryText = 'SELECT user_id,gp FROM user ORDER BY gp DESC LIMIT '.$limit;
$rows = $dbh->prepare($queryText)
   or return $rows->errstr;
$rows->execute()
   or return $rows->errstr;

$str .= '<p><b>Total GP in circulation:</b> ' . $totalGP . '</p>';

$str .= '<h3>'.$limit.' Richest Noders</h3>';
$str .= '<ol>';
my $richestUsersGP = 0;
my $isRecalculated;
while($row = $rows->fetchrow_arrayref)
{
   $str .= '<li>'.linkNode($$row[0]).' ('.$$row[1].'GP)';
   $isRecalculated = 0;
   foreach(@recalcUsers)
   {
      if ($_ == $$row[0])
      {
         $isRecalculated = 1;
         last;
      }
   }
   if (!$isRecalculated)
   {
      $str .= '<b>NOT A RECALCULATED USER</b>';
   }
   $str .= '</li>';
   $richestUsersGP += $$row[1];
}
$str .= '</ol><hr />';

$queryText = 'SELECT user_id,gp FROM user WHERE gp<>0 ORDER BY gp LIMIT '.$limit;
$rows = $dbh->prepare($queryText)
   or return $rows->errstr;
$rows->execute()
   or return $rows->errstr;
my $row;
$str .= '<h3>'.$limit.' Poorest Noders (ignore 0GP)</h3>';
$str .= '<ol>';
while($row = $rows->fetchrow_arrayref)
{
   $str .= '<li>'.linkNode($$row[0]).' ('.$$row[1].'GP)</li>';
}
$str .= '</ol>';

$str .= '<p>The top ' . $limit . ' users hold ' . ($richestUsersGP / $totalGP * 100) . '% of all the GP</p>';

#------------------------------------------------------------------------------------

my $key;
my $value;
my $v = getVars($USER);

$str .= '<table border="1">';
while (($key, $value) = each(%{$v})){
     $str .= '<tr><td>'.$key.'</td><td>'.$value.'</td></tr>';
     if ($key eq '2003-04-21')
     {
        delete($$v{$key});
     }
     if ($value eq '' || $value eq ' ')
     {
          delete($$v{$key});
     }
}
$str .= '</table>';

#my $keyToDelete = ' ';
#delete($$v{$keyToDelete});
setVars($USER, $v);
$VARS = $v;

return $str;

%]
