node.doctext:
[%
return linkNode($NODE, 'Send me a new user mail ', {'mailme' => 'true'}) . '&rarr;' unless $query->param('mailme');

#my $sql =    "select user_id, nick, email, passwd,realname from user where lasttime=0 and user_id > 1822772 group by email order by user_id desc";

my $sql = "select * from user where user_id=" . $$USER{user_id};

my $usr = $dbh->prepare($sql);

$usr->execute or return 'Oh dear. Database oops';

my $row=1;

my  $str = "<table style='width: 100%; border-top: 1px gray solid'><tr><th>User</th><th>email</th><th>Error?</th></tr>\n";


while(my $NEWUSER = $usr->fetchrow_hashref) {
   my ($n) = $DB->getNodeWhere({title=>'Everything2 New User Mail'}, $DB->getType('mail'));
   $$n{doctext} =~ s/\<name\>/$$NEWUSER{realname}/;
   $$n{doctext} =~ s/\<user\>/$$NEWUSER{nick}/;
   $$n{doctext} =~ s/\<passwd\>/$$NEWUSER{passwd}/;
   $$n{doctext} =~ s/\<site_name\>/$HTMLVARS{site_name}/;
   $$n{doctext} =~ s/\<homenode\>/$HTMLVARS{site_url}index\.pl\?node=$$NEWUSER{nick}\&type=user/;
   $$n{doctext} =~ s/\<site_url\>/$HTMLVARS{site_url}/;
   $$n{doctext} =~ s/\<login\>/$HTMLVARS{site_url}index\.pl\?node_id=$HTMLVARS{login_node}/;
   
   use Everything::MAIL;
   node2mail($$NEWUSER{email},$n);
   $n = getNodeById(getId($n), 'force');
   
   $str .= sprintf("\t<tr class='%s'><td><a href='%s'>%s</a></td><td>%s</td><td>%s</td></tr>\n",
                  $row ? 'oddrow' : 'evenrow',
                  urlGen({'node_id' => $$NEWUSER{user_id}}),
                  $$NEWUSER{nick},
                  $$NEWUSER{email},
                  $MAIL::Sender::Error
                  );
   $row= !$row;
   sleep(1);
}
$str .= '</table>';
$str;
%]

[%

return;
my ($n) = $DB->getNodeWhere({title=>'Everything2 New User Mail'}, $DB->getType('mail'));
$$n{doctext} =~ s/\<name\>/$$USER{realname}/;
$$n{doctext} =~ s/\<user\>/$$USER{title}/;
$$n{doctext} =~ s/\<passwd\>/pass/;
$$n{doctext} =~ s/\<site_name\>/e2/;
$$n{doctext} =~ s/\<homenode\>/$HTMLVARS{site_url}index\.pl\?node=$$USER{title}\&type=user/;
$$n{doctext} =~ s/\<site_url\>/$HTMLVARS{site_url}/;
$$n{doctext} =~ s/\<login\>/$HTMLVARS{site_url}index\.pl\?node_id=$HTMLVARS{login_node}/;
return linkNode($NODE, 'Mail ' . $$USER{realname} . ' &lt;' . $$USER{email}, {'mailme' => 'true'}) . '&gt;' unless $query->param('mailme');
use Everything::MAIL;
my $result = node2mail($$USER{email},  $n);
$n = getNodeById(getId($n), 'force');
my $iii;
$iii = 'yup' unless $Mail::Sender::Error;
'Result: ' . $Mail::Sender::Error . ($Mail::Sender::Error ? 'error' : 'ok'). $iii;

%]
