node.doctext:
[%

return "Not ready for primetime.";

my $uid = getId($USER);
my $isRoot = exists $HTMLVARS{group_gods}{$uid};
return unless $isRoot;

my $userTitle;
my $userX;
my ($formstr,$finalstr);
my ($newUserName, $oldUserName);

$formstr.=htmlcode('openform','');
$formstr.="User Name To Replace: ". $query->textfield("oldname",""). "<br />";
$formstr.="New User Name: ".$query->textfield("newname",""). "<br />";
$formstr.=htmlcode('closeform','');


if ((defined($query->param("newname")))&&(defined($query->param("oldname")))) {
	$oldUserName=$query->param("oldname");
	$oldUserName =~ s/\t//g;

	$userX=getNode($oldUserName,'user');
	return "No such user!".$formstr unless $userX;

	$newUserName=$query->param("newname");
	$newUserName =~ s/\t//g;

	my @allUserTypes = ('user', 'usergroup', 'superdoc', 'restricted_superdoc', 'nodetype', 'document', 'superdocnolinks');

	if (lc($oldUserName) ne lc($newUserName)) {
		my $olduser;
		foreach(@allUserTypes) {
			$olduser = getNode($newUserName, $_) unless $olduser;
		}
		if($olduser) {
			return 'Sorry, '.linkNode($olduser) ." already exists.  Pick a variation, or try another name...<p>\n" . $formstr;  
		}

		my $match=lc($newUserName);
		$match=~s/[_ ]/_/g;
		$match=~s/\\/\\\\/g;

		my $oldmatch=lc($oldUserName);
		$oldmatch=~s/[_ ]/_/g;
		$oldmatch=~s/\\/\\\\/g;
if ($oldmatch ne $match) {
		my $closeuser = 0;
		foreach(@allUserTypes) {
			$_ = 'type_nodetype='.getId(getType($_));
		}
		my $csr =$DB->sqlSelectMany('title','node','title like '.$dbh->quote($match).' AND (' . join(' OR ',@allUserTypes) . ')');

		my $match2 = lc($newUserName);
		$match2 =~ s/[_ ]/[_ ]/g;
		$match2 =~ s/\\/\\\\/g;
		while(my ($u) = $csr->fetchrow) {
			next unless $u =~ /^$match2$/i;
			$closeuser = 1;
		}
		if($closeuser){
			return "Sorry, the new name clashes with a username which already exists.  Pick a variation, or try another name...<p>\n" . $formstr;  
		}
}
	}


#TODO useralias table check, once that table is made

unless ($newUserName =~ /\w/) {
	return 'Uh...  how about some more letters?' . $formstr;
}

if($newUserName =~ /[\[\]\<\>\&]/) {
	return "That username has a special character in it.  Try something else.<p>" . $formstr;

}

if(($newUserName =~ /^\s/) or ($newUserName =~ /\s$/)) {
	return "Sorry, the new name may not have spaces at either end." . $formstr;
}

if ($newUserName =~ /\_/ && $newUserName =~ / /) {
	return 'Usernames cannot contain both spaces and underscores.' . $formstr;
}

if($newUserName =~ /\s\s/) {
	return "Please don't use multiple spaces in a row.<p>" . $formstr;
}

$$userX{title} = $newUserName;
updateNode($userX,-1);
$finalstr=$oldUserName." has been renamed to ".$newUserName.".";
}




return $finalstr." ".$formstr;

%]
