node.doctext:
[%
  return unless isGod($USER);
  my $str = htmlcode('openform');
  $str.='<blockquote>And what has' . $query->textfield('heavenuser').' been up to?';
  $str.='</blockquote>Or<blockquote>who killed' . $query->textfield('heaventitle').' ? ( ';
  $str.=$query->checkbox(-name=>'loose').' )</blockquote>';
  $str.='sort by ('.$query->radio_group(-name=>'sortfield',-default=>'title',-values=>['title','date']);
  $str.=') in ('.$query->radio_group(-name=>'ascdesc',-default=>'ascending',-values=>['ascending','descending']).') order<br />';
  my @offset = (0..25);
  foreach (@offset) { $_ *= 200 }
  $str.='offset: '. $query->popup_menu('offset', \@offset);
  my @limit = (1..10);
  foreach (@limit) { $_ *= 50 }


  $str.='limit: '. $query->popup_menu('limit', \@limit);
  $str.=htmlcode('closeform');

%]

<table width="100%">
<tr><th>Time</th><th>Title</th><th>Author User</th><th>Rep</th></tr>

[%
  return unless isGod($USER); 
  my $offset = int($query->param('offset'));
  $offset ||= 0;
  my $limit = int($query->param('limit'));
  $limit ||= 100;
  my $U = $USER;
  $U = getNode($query->param('heavenuser'),'user') if $query->param('heavenuser') and isGod($USER);
  my $sortfield = $query->param('sortfield');
  $sortfield = 'title' unless($sortfield eq 'date' and $sortfield = 'createtime');
  $sortfield.= " DESC" if ($query->param('ascdesc') eq 'descending');
  my $heaventitle = $query->param('heaventitle');
  my $whereclause;
  if ($heaventitle) {
    my $matchtype = '=';
    if ($query->param('loose') eq 'on') {
      $matchtype = 'like';
      $heaventitle .= '%';
    }
    $heaventitle = $dbh->quote($heaventitle);
    $whereclause = 'type_nodetype='.getId(getType('writeup'))." and title $matchtype $heaventitle";
    # return "This feature requires table re-indexing, and is unavailable at present. I would have used:<br /><i>$whereclause</i><br /> as the `where clause'";
  } elsif ($U) {
    $whereclause = 'type_nodetype='.getId(getType('writeup')).' and killa_user='.getId($U);
  } else {
    return 'Hmm... no data.  See you later.';     
  }

# my $csr = $DB->sqlSelectMany('*', 'heaven', 'type_nodetype='.getId(getType('writeup')).' and killa_user='.getId($U), "order by title limit $offset, $limit");

  my $csr = $DB->sqlSelectMany('*', 'heaven', $whereclause, "order by $sortfield limit $offset, $limit");
  my $num = $DB->sqlSelect('count(*)', 'heaven', 'type_nodetype='.getId(getType('writeup')).' and killa_user='.getId($U));
  
  my $str;
  my $count;
  my $T = getNode('Node Heaven Visitation', 'superdoc');
 
  while (my $N = $csr->fetchrow_hashref) {
    $count++;
    $str.="<tr><td>$$N{createtime}</td><td>"
      .linkNode($T, $$N{title}, {visit_id => $$N{node_id}})
      .'</td><td>'.linkNode($$N{author_user}).'</td><td>'.$$N{reputation}."</td><tr>\n";  
  } 

  "<tr><td>Kill count: $num</td></tr>".$str;

%]
</table>
